import petrovich from 'petrovich';
import { saveAs } from 'file-saver';
import PizZip from 'pizzip';
import Docxtemplater from 'docxtemplater';
import word from './protocol.docx';

const generateProtocolsFunction = async ({ 
    composition_of_sec, 
    selectedGroup,
    groupData,
    data
}) => {
    const chairman = composition_of_sec.find(comp => comp.chairman_of_SEC === true);
    const members_list = composition_of_sec.filter(sec => sec.chairman_of_SEC === false);

    const getInitials = (firstName, patronymic) => {
        return `${firstName.charAt(0)}.${patronymic.charAt(0)}.`;
    };

    const np = getInitials(chairman.user.first_name, chairman.user.patronymic);

    try {
        const response = await fetch(word);
        if (!response.ok) throw new Error('Ошибка сети');
        const arrayBuffer = await response.arrayBuffer();

        // Создаем новый экземпляр PizZip для хранения файлов в архиве
        const zip = new PizZip();

        for (const item of groupData) {
            const { request, work_protection } = item;
            const student = request.student;
            const teacher = request.teacher;
            const theme = request.theme;
            const [hours_start, min_start] = work_protection.time.split(':');
            const full_name_student = {
                last: student.user.last_name,
                first: student.user.first_name,
                middle: student.user.patronymic,
            };
            let fullnameStudent = petrovich(full_name_student, 'accusative');
            fullnameStudent = `${fullnameStudent.last} ${fullnameStudent.first} ${fullnameStudent.middle}`;
            // Вычисляем время окончания: добавляем 20 минут к времени начала
            const startTime = new Date();
            startTime.setHours(hours_start);
            startTime.setMinutes(min_start);
            startTime.setMinutes(startTime.getMinutes() + 20);

            const hours_end = startTime.getHours();
            const min_end = startTime.getMinutes();

            const dataToRender = {
                protocol_number: work_protection.protocol_number,
                group_protection_date: new Date(work_protection.date).toLocaleDateString('ru-RU'),
                hours_start: hours_start,
                min_start: min_start,
                hours_end: hours_end,
                min_end: min_end,
                chairman_last_name: chairman.user.last_name,
                chairman_np: np,
                chairman_degree: chairman.employee_information.academic_degree,
                chairman_job_title: chairman.employee_information.job_title,
                members_list: members_list.map(member => ({
                    members_last_name: member.user.last_name,
                    members_np: getInitials(member.user.first_name, member.user.patronymic),
                    members_degree: member.employee_information.academic_degree || '',
                    members_job_title: member.employee_information.job_title,
                    members_place_of_work: member.employee_information.place_of_work,
                })),
                student_fullname: fullnameStudent,
                specialityCode: student.group.speciality.code,
                specialtityProgram: student.group.speciality.name,
                theme: request.theme_str,
                teacher_last_name: teacher.user.last_name,
                teacher_np: getInitials(teacher.user.first_name, teacher.user.patronymic),
                teacher_degree: teacher.employee_information.academic_degree,
                teacher_job_title: teacher.employee_information.job_title,
                reviewer_last_name: theme.reviewer.user.last_name ? theme.reviewer.user.last_name : '' , 
                reviewer_np: theme.reviewer.user.last_name ? getInitials(theme.reviewer.user.first_name, theme.reviewer.user.patronymic) : ' ',
                reviewer_degree: theme.reviewer.employee_information.academic_degree ? theme.reviewer.employee_information.academic_degree : ' ', 
                reviewer_job_title: theme.reviewer.employee_information.job_title ? theme.reviewer.employee_information.job_title : ' ', 
                student_last_name: student.user.last_name,
                student_np: getInitials(student.user.first_name, student.user.patronymic),
                secretary_last_name: data.user.last_name,
                secretary_np: getInitials(data.user.first_name, data.user.patronymic),
                duration: 20, 
            };

            // Создаем новую инстанцию Docxtemplater для каждого студента
            const doc = new Docxtemplater(new PizZip(arrayBuffer));

            doc.loadZip(arrayBuffer);
            
            doc.setData(dataToRender);
            doc.render();

            // Получаем сгенерированный файл
            const generatedDoc = doc.getZip().generate({ type: 'blob' });

            // Добавляем сгенерированный файл в архив
            zip.file(`Протокол_${student.user.last_name}_${student.user.first_name}.docx`, generatedDoc);
        }

        // Генерация архива в формате blob
        const zipBlob = await zip.generateAsync({ type: 'blob' });

        // Сохранение архива с помощью FileSaver
        saveAs(zipBlob, 'Протоколы.zip');
    } catch (error) {
        console.error('Ошибка загрузки файла:', error);
    }
};

export default generateProtocolsFunction;
