import petrovich from 'petrovich';
import { saveAs } from 'file-saver';
import PizZip from 'pizzip';
import Docxtemplater from 'docxtemplater';
import word from './protocol.docx';

const generateProtocolsFunction = async ({ 
    composition_of_sec, 
    selectedGroup,
    groupData,
    data,
    request
}) => {
    // Извлекаем председателя и членов СЭК
    const chairman = composition_of_sec.member_of_SEC.find(comp => comp.chairman_of_SEC === true);
    const members_list = composition_of_sec.member_of_SEC.filter(sec => sec.chairman_of_SEC === false);

    // Функция для получения инициалов (Фамилия И.О.)
    const getInitials = (firstName, patronymic) => {
        return `${firstName.charAt(0)}. ${patronymic.charAt(0)}.`;
    };

    try {
        // Загружаем шаблон документа
        const response = await fetch(word); // Путь к вашему файлу шаблона
        if (!response.ok) throw new Error('Ошибка сети');
        const arrayBuffer = await response.arrayBuffer();

        // Проходимся по каждому элементу groupData (предполагаем, что это массив)
        for (const item of groupData) {
            const student = item.student;
            const request = request.request_list.find(req => req.is_accepted_request); 

            if (!request) {
                console.warn(`Для студента ${student.last_name} ${student.first_name} не найдена принятая заявка.`);
                continue; // Пропускаем студента без принятой заявки
            }

            const teacher = request.teacher;
            const theme = request.theme;

            // Вычисляем время окончания: добавляем 20 минут к времени начала
            const [hours_start, min_start] = item.work_protection.time.split(':');
            const startTime = new Date();
            startTime.setHours(hours_start);
            startTime.setMinutes(min_start);
            startTime.setMinutes(startTime.getMinutes() + 20);

            const hours_end = startTime.getHours();
            const min_end = startTime.getMinutes();

            // Формируем объект с данными для подстановки в шаблон
            const dataToRender = {
                protocol_number: item.work_protection.protocol_number,
                group_protection_date: new Date(item.work_protection.date).toLocaleDateString('ru-RU'),
                hours_start: hours_start,
                min_start: min_start,
                hours_end: hours_end,
                min_end: min_end,
                chairman_last_name: chairman.user.last_name,
                chairman_np: getInitials(chairman.user.first_name, chairman.user.patronymic),
                chairman_degree: chairman.employee_information.academic_degree,
                chairman_job_title: chairman.employee_information.job_title,
                members_list: members_list.map(member => ({
                    members_last_name: member.user.last_name,
                    members_np: member.user ? getInitials(member.user.first_name, member.user.patronymic) : '',
                    members_degree: member.employee_information.academic_degree || '',
                    members_job_title: member.employee_information.job_title,
                    members_place_of_work: member.employee_information.place_of_work,
                })),
                student_fullname: `${student.last_name} ${student.first_name} ${student.patronymic}`,
                specialityCode: student.group.speciality.code,
                specialtityProgram: student.group.speciality.name,
                theme: theme ? theme.name : '',
                teacher_last_name: teacher.last_name,
                teacher_np: getInitials(teacher.first_name, teacher.patronymic),
                teacher_degree: teacher.employee_information.academic_degree,
                teacher_job_title: teacher.employee_information.job_title,
                reviewer_last_name: theme && theme.reviewer.user.last_name ? theme.reviewer.user.last_name : '',
                reviewer_np: theme && theme.reviewer.user.last_name ? getInitials(theme.reviewer.user.first_name, theme.reviewer.user.patronymic) : '',
                reviewer_degree: theme && theme.reviewer.employee_information.academic_degree ? theme.reviewer.employee_information.academic_degree : '',
                reviewer_job_title: theme && theme.reviewer.employee_information.job_title ? theme.reviewer.employee_information.job_title : '',
                student_last_name: student.last_name,
                student_np: getInitials(student.first_name, student.patronymic),
                secretary_last_name: data.user.last_name,
                secretary_np: getInitials(data.user.first_name, data.user.patronymic),
                duration: 20,
            };

            // Создаем новую инстанцию Docxtemplater для каждого студента
            const zip = new PizZip(arrayBuffer);
            const doc = new Docxtemplater(zip);

            doc.setData(dataToRender);
            doc.render();

            // Генерируем файл и сохраняем его
            const output = doc.getZip().generate({ type: 'blob' });
            saveAs(output, `Протокол_${student.last_name}_${student.first_name}.docx`);
        }
    } catch (error) {
        console.error('Ошибка загрузки файла:', error);
    }
};

export default generateProtocolsFunction;
