import React, { useState } from 'react';
import { Form, Button, Row, Col } from 'react-bootstrap';
import plus from '../../../../img/plus.png';
import del from '../../../../img/del.png';
import edit from '../../../../img/edit.png';
import styles from '../Teacher/EditThemeList.module.css';

const EditThemesListSecretaryForm = ({ theme_list: initialThemesList, teachers_list }) => {
  const [themesList, setThemesList] = useState(initialThemesList || []);
  const [editingIndex, setEditingIndex] = useState(null);
  const [editingData, setEditingData] = useState(null);
  const [editFormVisibleIndex, setEditFormVisibleIndex] = useState(null);
  const [isLinked, setIsLinked] = useState(false);
  const [filterAuthor, setFilterAuthor] = useState('');
  const [authorType, setAuthorType] = useState('');
  const [selectedTeacher, setSelectedTeacher] = useState(null);
  
  const addRow = () => {
    const newRow = {
      id: themesList.length ? Math.max(...themesList.map(item => item.id)) + 1 : 1,
      name: '',
      teacher: null,
      company: null,
      free: true,
      fqw_by_application: '',
      reviewer: null,
      by_student: false,
      department: false,
    };
    
    const updatedThemesList = [newRow, ...themesList];
    setThemesList(updatedThemesList);
    setEditingIndex(newRow.id);
    setEditingData(newRow);
    setAuthorType('');
  };

  const handleSave = () => {
    const updatedThemesList = themesList.map(item =>
      item.id === editingIndex ? { ...item, ...editingData } : item
    );
    setThemesList(updatedThemesList);
    setEditFormVisibleIndex(null);
    setEditingIndex(null);
    setEditingData(null);
  };

  const handleFilterAuthorChange = (e) => {
    setFilterAuthor(e.target.value);
  };

  const filteredThemesList = themesList.filter(item => {
    if (!item.teacher && !item.by_student && !item.department) {
      return true;
    }

    if (item.teacher) {
      return `${item.teacher.last_name} ${item.teacher.first_name} ${item.teacher.patronymic}`.toLowerCase().includes(filterAuthor.toLowerCase());
    } else if (item.by_student) {
      return 'предложено студентом'.toLowerCase().includes(filterAuthor.toLowerCase());
    } else if (item.department) {
      return 'кафедра сии'.toLowerCase().includes(filterAuthor.toLowerCase());
    } else {
      return true;
    }
  });

  const handleEditClick = (index) => {
    const themeToEdit = themesList.find(item => item.id === index);
    setEditingIndex(index);
    setEditingData(themeToEdit);
    setEditFormVisibleIndex(index);
    setIsLinked(!!themeToEdit.company);
    setAuthorType(
      themeToEdit.by_student ? 'student' :
      themeToEdit.department ? 'department' :
      'teacher'
    );
  };

  const handleCancel = () => {
    setEditFormVisibleIndex(null);
    if (editingIndex === null) {
      const newData = themesList.filter(item => item.id !== editingIndex);
      setThemesList(newData);
    }
    setEditingIndex(null);
  };

  const handleDeleteClick = (index) => {
    const newData = themesList.filter(item => item.id !== index);
    setThemesList(newData);
    if (editFormVisibleIndex === index) {
      setEditFormVisibleIndex(null);
    }
    setEditingIndex(null);
  };

  const handleInputChange = (e) => {
    const { name, value, type, checked } = e.target;
    const updatedEditingData = {
      ...editingData,
      [name]: type === 'checkbox' ? checked : value,
    };
    setEditingData(updatedEditingData);
  };

  const handleCompanyChange = (e) => {
    const { name, value } = e.target;
    const updatedEditingData = {
      ...editingData,
      company: {
        ...editingData.company,
        [name]: value,
      },
    };
    setEditingData(updatedEditingData);
  };

  const handleReviewerChange = (e) => {
    const { name, value } = e.target;
    const updatedReviewer = {
      ...editingData.reviewer,
      [name]: value,
    };
    const updatedEditingData = {
      ...editingData,
      reviewer: updatedReviewer,
    };
    setEditingData(updatedEditingData);
  };

  const handleRadioChange = (value) => {
    setIsLinked(value === 'yes');
    if (value === 'no') {
      const updatedEditingData = {
        ...editingData,
        company: null,
      };
      setEditingData(updatedEditingData);
    }
  };

  const handleTeacherSelectChange = (e) => {
    const teacherId = e.target.value;
    const selectedTeacher = teachers_list.find(teacher => teacher.user.id === parseInt(teacherId));
  
    setSelectedTeacher(selectedTeacher);
  
    const updatedEditingData = {
      ...editingData,
      teacher: selectedTeacher ? selectedTeacher.user : null
    };
  
    setEditingData(updatedEditingData);
  };
  
  const handleAuthorTypeChange = (e) => {
    const { value } = e.target;
    setAuthorType(value);
    
    setSelectedTeacher(null);
    
    switch (value) {
      case 'teacher':
        setEditingData(prevState => ({
          ...prevState,
          by_student: false,
          department: false
        }));
        break;
      case 'department':
        setEditingData(prevState => ({
          ...prevState,
          by_student: false,
          department: true
        }));
        break;
      case 'student':
        setEditingData(prevState => ({
          ...prevState,
          by_student: true,
          department: false
        }));
        break;
      default:
        break;
    }
  };
  
  const getTeacherName = (item) => {
    if (item.teacher) {
      return `${item.teacher.last_name} ${item.teacher.first_name} ${item.teacher.patronymic}`;
    } else if (item.by_student) {
      return 'Предложено студентом';
    } else if (item.department) {
      return 'Кафедра СИИ';
    } else {
      return '';
    }
  };
  
  return (
    <>
      <h4 className="mb-4 text-center">Редактирование списка тем ВКР</h4>
      <Form.Group as={Row} controlId="filterAuthor" className="justify-content-center">
        <Form.Label column sm={2} className="text-center my-3">Фильтр по автору</Form.Label>
        <Col sm={6}>
          <Form.Control
            type="text"
            value={filterAuthor}
            onChange={handleFilterAuthorChange}
            placeholder="Введите имя автора для фильтрации"
            className={styles.block_form}
          />
        </Col>
      </Form.Group>

      <div className={styles.catch}>
        <table className={styles.table_themes}>
          <thead className="text-center">
            <tr>
              <th rowSpan="2" width="10%">Автор</th>
              <th rowSpan="2" width="50%">Наименование темы ВКР</th>
              <th colSpan="4" className="no-filter">Организация</th>
              <th rowSpan="2" width="5%">ВКР по заявке</th>
              <th rowSpan="2" width="5%">Ревьювер</th>
              <th rowSpan="2">Занята</th>
              <th rowSpan="2" className="no-filter">Действия</th>
              <th className="no-filter">
                <img id="addRowButton" src={plus} onClick={addRow} width="30" height="30" alt="Add Row" />
              </th>
            </tr>
            <tr>
              <th>Наименование</th>
              <th>Ответственный</th>
              <th>Должность</th>
              <th>Дополнительно</th>
            </tr>
          </thead>
          <tbody>
            {filteredThemesList.map((item) => (
              <React.Fragment key={item.id}>
                <tr>
                  <td>{getTeacherName(item)}</td>
                  <td>{item.name}</td>
                  <td>{item.company ? item.company.name : ''}</td>
                  <td>{item.company ? `${item.company.last_name_of_responsible} ${item.company.first_name_of_responsible} ${item.company.patronymic_of_responsible}` : ''}</td>
                  <td>{item.company ? item.company.job_title_of_responsible : ''}</td>
                  <td>{item.company ? item.company.additional_information : ''}</td>
                  <td>{item.fqw_by_application}</td>
                  <td>{item.reviewer ? `${item.reviewer.last_name} ${item.reviewer.first_name} ${item.reviewer.patronymic}` : ''}</td>
                  <td>{item.free ? 'Нет' : 'Да'}</td>
                  <td>
                    <img src={edit} onClick={() => handleEditClick(item.id)} width="20" height="20" alt="Edit" />
                    <img src={del} onClick={() => handleDeleteClick(item.id)} width="20" height="20" alt="Delete" />
                  </td>
                </tr>
                {editFormVisibleIndex === item.id && (
                  <tr className={styles.edition} id="editFormRow">
                    <td colSpan="11">
                      <Form>
                        <Form.Row>
                          <Form.Group as={Col} controlId="authorType">
                            <Form.Label>Автор</Form.Label>
                            <Form.Control as="select" value={authorType} onChange={handleAuthorTypeChange}>
                              <option value="">Выберите тип автора</option>
                              <option value="teacher">Преподаватель</option>
                              <option value="student">Предложено студентом</option>
                              <option value="department">Кафедра СИИ</option>
                            </Form.Control>
                          </Form.Group>

                          {authorType === 'teacher' && (
                            <Form.Group as={Col} controlId="teacherSelect">
                              <Form.Label>Преподаватель</Form.Label>
                              <Form.Control as="select" value={selectedTeacher ? selectedTeacher.user.id : ''} onChange={handleTeacherSelectChange}>
                                <option value="">Выберите преподавателя</option>
                                {teachers_list.map((teacher) => (
                                  <option key={teacher.user.id} value={teacher.user.id}>
                                    {`${teacher.user.last_name} ${teacher.user.first_name} ${teacher.user.patronymic}`}
                                  </option>
                                ))}
                              </Form.Control>
                            </Form.Group>
                          )}
                        </Form.Row>

                        <Form.Group controlId="name">
                          <Form.Label>Наименование темы ВКР</Form.Label>
                          <Form.Control
                            type="text"
                            name="name"
                            value={editingData.name}
                            onChange={handleInputChange}
                          />
                        </Form.Group>

                        <Form.Group as={Row} controlId="isLinked">
                          <Form.Label as="legend" column sm={2}>
                            Связано с организацией
                          </Form.Label>
                          <Col sm={10}>
                            <Form.Check
                              type="radio"
                              label="Да"
                              name="isLinked"
                              id="isLinkedYes"
                              value="yes"
                              checked={isLinked}
                              onChange={() => handleRadioChange('yes')}
                            />
                            <Form.Check
                              type="radio"
                              label="Нет"
                              name="isLinked"
                              id="isLinkedNo"
                              value="no"
                              checked={!isLinked}
                              onChange={() => handleRadioChange('no')}
                            />
                          </Col>
                        </Form.Group>

                        {isLinked && (
                          <>
                            <Form.Group controlId="companyName">
                              <Form.Label>Наименование организации</Form.Label>
                              <Form.Control
                                type="text"
                                name="name"
                                value={editingData.company ? editingData.company.name : ''}
                                onChange={handleCompanyChange}
                              />
                            </Form.Group>

                            <Form.Group controlId="companyResponsible">
                              <Form.Label>Ответственный</Form.Label>
                              <Form.Control
                                type="text"
                                name="last_name_of_responsible"
                                placeholder="Фамилия"
                                value={editingData.company ? editingData.company.last_name_of_responsible : ''}
                                onChange={handleCompanyChange}
                              />
                              <Form.Control
                                type="text"
                                name="first_name_of_responsible"
                                placeholder="Имя"
                                value={editingData.company ? editingData.company.first_name_of_responsible : ''}
                                onChange={handleCompanyChange}
                              />
                              <Form.Control
                                type="text"
                                name="patronymic_of_responsible"
                                placeholder="Отчество"
                                value={editingData.company ? editingData.company.patronymic_of_responsible : ''}
                                onChange={handleCompanyChange}
                              />
                            </Form.Group>

                            <Form.Group controlId="companyJobTitle">
                              <Form.Label>Должность ответственного</Form.Label>
                              <Form.Control
                                type="text"
                                name="job_title_of_responsible"
                                value={editingData.company ? editingData.company.job_title_of_responsible : ''}
                                onChange={handleCompanyChange}
                              />
                            </Form.Group>

                            <Form.Group controlId="companyAdditionalInfo">
                              <Form.Label>Дополнительная информация</Form.Label>
                              <Form.Control
                                type="text"
                                name="additional_information"
                                value={editingData.company ? editingData.company.additional_information : ''}
                                onChange={handleCompanyChange}
                              />
                            </Form.Group>
                          </>
                        )}

                        <Form.Group controlId="fqw_by_application">
                          <Form.Label>ВКР по заявке</Form.Label>
                          <Form.Control
                            type="text"
                            name="fqw_by_application"
                            value={editingData.fqw_by_application}
                            onChange={handleInputChange}
                          />
                        </Form.Group>

                        <Form.Group controlId="reviewer">
                          <Form.Label>Ревьювер</Form.Label>
                          <Form.Control
                            type="text"
                            name="last_name"
                            placeholder="Фамилия"
                            value={editingData.reviewer ? editingData.reviewer.last_name : ''}
                            onChange={handleReviewerChange}
                          />
                          <Form.Control
                            type="text"
                            name="first_name"
                            placeholder="Имя"
                            value={editingData.reviewer ? editingData.reviewer.first_name : ''}
                            onChange={handleReviewerChange}
                          />
                          <Form.Control
                            type="text"
                            name="patronymic"
                            placeholder="Отчество"
                            value={editingData.reviewer ? editingData.reviewer.patronymic : ''}
                            onChange={handleReviewerChange}
                          />
                        </Form.Group>

                        <Button variant="secondary" onClick={handleCancel} className="mr-2">
                          Отмена
                        </Button>
                        <Button variant="primary" onClick={handleSave}>
                          Сохранить
                        </Button>
                      </Form>
                    </td>
                  </tr>
                )}
              </React.Fragment>
            ))}
          </tbody>
        </table>
      </div>
    </>
  );
};

export default EditThemesListSecretaryForm;
