import React from 'react';
import { Table } from 'react-bootstrap';

function ViewProtectionScheduleTable({ protection_schedule }) {
  // Helper function to convert date format
  const formatDate = (dateString) => {
    const months = [
      'января', 'февраля', 'марта', 'апреля', 'мая', 'июня',
      'июля', 'августа', 'сентября', 'октября', 'ноября', 'декабря'
    ];
    const day = dateString.slice(0, 2);
    const month = months[parseInt(dateString.slice(2, 4), 10) - 1];
    const year = dateString.slice(4, 8);
    return `${day} ${month} ${year}`;
  };

  const generateHeading = (group) => {
    const { name, speciality } = group;
    const { code: specialityCode, name: specialityName, field_of_study } = speciality;
    const { code: fieldOfStudyCode, name: fieldOfStudyName } = field_of_study;

    if (name.includes('Б')) {
      return `Бакалавры по специальности ${specialityCode} "${specialityName}" по программе ${fieldOfStudyCode} "${fieldOfStudyName}"`;
    } else if (name.includes('М')) {
      return `Магистры по специальности ${specialityCode} "${specialityName}" по программе ${fieldOfStudyCode} "${fieldOfStudyName}"`;
    } else {
      return `Группа ${name}`;
    }
  };

  // Group by date first
  const groupedByDate = protection_schedule.reduce((acc, item) => {
    const date = item.date;
    if (!acc[date]) {
      acc[date] = [];
    }
    acc[date].push(item);
    return acc;
  }, {});

  // Get unique groups and group items by group name
  const groupItemsByGroupName = protection_schedule.reduce((acc, item) => {
    const groupName = item.group.name;
    if (!acc[groupName]) {
      acc[groupName] = [];
    }
    acc[groupName].push(item);
    return acc;
  }, {});

  return (
    <>
      <h4 className="mb-4 text-center">График защит ВКР</h4>
      {Object.keys(groupedByDate).map((date, dateIndex) => {
        const groupsForDate = groupedByDate[date];
        const formattedDate = formatDate(date);

        // Create a set of unique group names for the current date
        const uniqueGroupNames = [...new Set(groupsForDate.map(item => item.group.name))];

        return (
          <div key={dateIndex} style={{ marginBottom: '20px' }}>
            {uniqueGroupNames.map((groupName, groupIndex) => {
              const group = groupsForDate.find(item => item.group.name === groupName).group;
              const heading = generateHeading(group);
              return <h6 key={`heading-${groupName}-${groupIndex}`}>{heading}</h6>;
            })}

            <Table bordered style={{ width: '100%' }}>
              <thead>
                <tr>
                  <th>ПП</th>
                  <th>Дата</th>
                  <th>Время</th>
                  <th>Аудитория</th>
                  <th>Уч. группа</th>
                </tr>
              </thead>
              <tbody>
                {groupsForDate.map((item, idx) => (
                  <tr key={item.id}>
                    <td>{idx + 1}</td>
                    <td>{formattedDate}</td>
                    <td>9:00</td>
                    <td>{item.audience_number}</td>
                    <td>{item.group.name}</td>
                  </tr>
                ))}
              </tbody>
            </Table>
          </div>
        );
      })}
      {Object.keys(groupItemsByGroupName).map((groupName, groupIndex) => {
        const groupItems = groupItemsByGroupName[groupName];

        // Group by dates for the same group
        const groupedByDateWithinGroup = groupItems.reduce((acc, item) => {
          const date = item.date;
          if (!acc[date]) {
            acc[date] = [];
          }
          acc[date].push(item);
          return acc;
        }, {});

        const group = groupItems[0].group;
        const heading = generateHeading(group);

        return (
          <div key={`group-${groupIndex}`} style={{ marginBottom: '20px' }}>
            <h6>{heading}</h6>
            <Table bordered style={{ width: '100%' }}>
              <thead>
                <tr>
                  <th>ПП</th>
                  <th>Дата</th>
                  <th>Время</th>
                  <th>Аудитория</th>
                  <th>Уч. группа</th>
                </tr>
              </thead>
              <tbody>
                {Object.keys(groupedByDateWithinGroup).map((date, dateIndex) =>
                  groupedByDateWithinGroup[date].map((item, idx) => (
                    <tr key={item.id}>
                      <td>{dateIndex * groupedByDateWithinGroup[date].length + idx + 1}</td>
                      <td>{formatDate(item.date)}</td>
                      <td>9:00</td>
                      <td>{item.audience_number}</td>
                      <td>{groupName}</td>
                    </tr>
                  ))
                )}
              </tbody>
            </Table>
          </div>
        );
      })}
    </>
  );
}

export default ViewProtectionScheduleTable;
