import React, { Component } from 'react';
import { Button, Container, Row, Form, Col } from 'react-bootstrap';
import styles from './AutentificationForm.module.css';
import enter from './enter.png';
import {BrowserRouter as Router, Routes, Link, Route, useRouteMatch} from 'react-router-dom'
import ProfileStudent from '../../pages/ProfileStudent'

export default class AuthentificationForm extends Component {
  
  render() {
    let { path, url } = useRouteMatch();
    return (
 
          <Container className={styles.container}>
            <div className={styles.card_fone}>
              <Row>
                <Col md={6} className="mt-4">
                  <h4 className="text-center mb-4">Авторизоваться</h4>
                  <div className={`${styles.auth_form_left} text-center py-4 mx-md-4`}>
                    <Form className="py-5 px-1">
                      <div className="form-group">
                        <input type="text" className={styles.auth_form_input} id="username" placeholder="Логин" />
                      </div>
                      <div className="form-group">
                        <input type="password" className={styles.auth_form_input} id="password" placeholder="Пароль" />
                      </div>
                      <Link to={`${url}/profile`} className={`${styles.button} px-5 mx-5`}> Войти </Link>
                    </Form>
                  </div>
                </Col>
                <Col md={6} className="align-items-center">
                  <div className={`${styles.auth_form_right} text-center p-3`}>
                    <img src={enter} className={styles.enter} width="70%" alt="Icon" loading="lazy" />
                  </div>
                </Col>
              </Row>
            </div>
          </Container>
        
    );
  }
}
