import React, { useState } from 'react';
import { useTable } from 'react-table';
import * as XLSX from 'xlsx';
import { saveAs } from 'file-saver';
import styles from '../../../container.module.css';

function SecretaryHugeTable({ info_for_table }) {
  const [tableData, setTableData] = useState(info_for_table.requests);
  const [filters, setFilters] = useState({});

  const columns = React.useMemo(
    () => [
      {
        Header: '№ п/п',
        accessor: (row, index) => index + 1
      },
      {
        Header: 'ср.б.',
        accessor: 'student.average_score',
        Cell: ({ row }) => {
          const { index, original } = row;
          return (
            <input
              type="text"
              value={original.student.average_score}
              onChange={(e) => handleInputChange(e, index, 'student.average_score')}
            />
          );
        }
      },
      {
        Header: '№ протокола',
        accessor: 'protocol_number'
      },
      {
        Header: 'Дата протокола',
        accessor: 'protocol_date'
      },
      {
        Header: 'Фамилия, Имя, Отчество',
        accessor: (row) => `${row.student.surname} ${row.student.name} ${row.student.patronymic}`
      },
      {
        Header: 'Тема диплома',
        accessor: 'theme.name'
      },
      {
        Header: 'Руководитель диплома',
        accessor: (row) => `${row.teacher.surname} ${row.teacher.name} ${row.teacher.patronymic}`
      },
      {
        Header: 'Консультант',
        accessor: (row) => `${row.consultant.surname} ${row.consultant.name} ${row.consultant.patronymic}`
      },
      {
        Header: 'Рецензент',
        accessor: (row) => `${row.theme.reviewer.surname} ${row.theme.reviewer.name} ${row.theme.reviewer.patronymic}`
      },
      {
        Header: 'Организация',
        accessor: (row) => `${row.theme.organization.name} ${row.theme.organization.last_name_of_responsible} ${row.theme.organization.first_name_of_responsible} ${row.theme.organization.patronymic_name_of_responsible} ${row.theme.organization.job_title_of_resposible} ${row.theme.organization.additional_information}`
      },
      {
        Header: '№ зачетки',
        accessor: 'student.record_book_number'
      },
      {
        Header: 'Телефон',
        accessor: 'student.phone'
      },
      {
        Header: 'Email',
        accessor: 'student.email'
      },
      {
        Header: 'Бюджет/платный',
        accessor: 'student.basis_of_study'
      },
      {
        Header: 'Оценка на предзащите/преддипломная практика',
        accessor: 'preprotection_grade'
      },
      {
        Header: 'Оценка на защите',
        accessor: 'workProtecton.final_grade'
      },
      {
        Header: 'Дата рождения',
        accessor: 'student.date_of_birth'
      },
      {
        Header: 'Год окончания предыдущего образования',
        accessor: 'student.year_of_prev_education_completion'
      },
      {
        Header: 'Форма предыдущего образования',
        accessor: 'student.form_of_prev_education'
      },
      {
        Header: 'Год поступления',
        accessor: 'student.year_of_admission'
      },
      {
        Header: 'Особые отметки',
        accessor: 'student.special_marks'
      },
      {
        Header: 'Приказы: Допуск',
        accessor: 'admission'
      },
      {
        Header: 'Приказы: Тема',
        accessor: 'topic'
      },
      {
        Header: 'Приказы: Изменение темы',
        accessor: 'topic_change'
      },
      {
        Header: 'Приказы: Рецензент',
        accessor: 'reviewer'
      },
      {
        Header: 'Приказы: Отчисление',
        accessor: 'expulsion'
      },
      {
        Header: 'Приказы: С какого числа отчисление',
        accessor: 'expulsion_date'
      },
      {
        Header: 'Отметки о выполнении: Долги',
        accessor: 'debts'
      },
      {
        Header: 'Отметки о выполнении: Справка о выполнении учебного плана',
        accessor: 'curriculum_completion_certificate'
      },
      {
        Header: 'Отметки о выполнении: Сдана зачетка',
        accessor: 'record_book_submitted'
      },
      {
        Header: 'Отметки о выполнении: Сдана пояснительная записка',
        accessor: 'explanatory_note_submitted'
      },
      {
        Header: 'Отметки о выполнении: Красный диплом',
        accessor: 'red_diploma'
      },
      {
        Header: 'Отметки о выполнении: Согласие на публикацию',
        accessor: 'publication_consent'
      },
      {
        Header: 'Отметки о выполнении: Отзыв',
        accessor: 'feedback'
      },
      {
        Header: 'Отметки о выполнении: Рецензия',
        accessor: 'review'
      },
      {
        Header: 'Отметки о выполнении: Акт о внедрении',
        accessor: 'implementation_act'
      },
      {
        Header: 'Отметки о выполнении: Отчет о плагиате',
        accessor: 'plagiarism_report'
      },
      {
        Header: 'Отметки о выполнении: Заявление на последипломный отпуск',
        accessor: 'postgraduate_leave_application'
      },
      {
        Header: 'Отметки о выполнении: Рекомендован к поступлению',
        accessor: 'recommended_for_admission'
      },
      {
      Header: 'Вид ВКР',
      accessor: 'type_of_fqw'
      },
      {
      Header: 'ВКР по заявке',
      accessor: 'theme.fqw_by_application'
      },
      {
      Header: 'ВКР на англ. языке',
      accessor: 'fqw_in_english'
      },
      {
      Header: 'ВКР рекомендовано',
      accessor: 'fqw_recommended'
      },
      {
      Header: 'Наличие спец. условий',
      accessor: 'special_conditions'
      }
      ],
      []
      );
      
      const {
      getTableProps,
      getTableBodyProps,
      headerGroups,
      rows,
      prepareRow
      } = useTable({ columns, data: tableData });
      
      const handleInputChange = (e, rowIndex, field) => {
      const newData = [...tableData];
      newData[rowIndex][field] = e.target.value;
      setTableData(newData);
      };
      
      const handleDoubleClick = (e, rowIndex, field) => {
      const target = e.target;
      if (target.tagName === 'TD') {
      const input = document.createElement('input');
      input.type = 'text';
      input.value = target.innerText;
      input.addEventListener('blur', () => {
      const newData = [...tableData];
      newData[rowIndex][field] = input.value;
      setTableData(newData);
      target.innerText = input.value;
      });
      target.innerText = '';
      target.appendChild(input);
      input.focus();
      }
      };
      
      const exportToExcel = () => {
      const ws = XLSX.utils.table_to_sheet(document.getElementById('dataTable'));
      const wb = XLSX.utils.book_new();
      XLSX.utils.book_append_sheet(wb, ws, 'Data');
      XLSX.writeFile(wb, 'Data.xlsx');
      };
      
      const uniqueValues = {};
      tableData.forEach(row => {
      Object.keys(row).forEach(key => {
      if (!uniqueValues[key]) {
      uniqueValues[key] = new Set();
      }
      uniqueValues[key].add(row[key]);
      });
      });
      
      // Создание фильтров для каждого столбца
      const createFilters = () => {
      const newFilters = {};
      Object.keys(uniqueValues).forEach(key => {
      newFilters[key] = Array.from(uniqueValues[key]);
      });
      return newFilters;
      };
      
      // Фильтрация таблицы
      const filterData = (data, filters) => {
      return data.filter(row => {
      return Object.keys(filters).every(key => {
      const selectedValue = filters[key];
      return selectedValue === '' || String(row[key]) === selectedValue;
      });
      });
      };
      
      const handleFilterChange = (e, field) => {
      const selectedValue = e.target.value;
      setFilters({ ...filters, [field]: selectedValue });
      };
      
      const filteredData = filterData(tableData, filters);
      
      return (
      <>
      <h4 className="mb-4 text-center">Таблица данных</h4>
      <Button onClick={exportToExcel} className="mb-4">Export to Excel</Button>
      <div className="d-flex justify-content-between">
    {Object.keys(createFilters()).map((key, index) => (
      <div key={index}>
        <div>{key}</div>
        <select onChange={(e) => handleFilterChange(e, key)} value={filters[key] || ''}>
          <option value="">All</option>
          {createFilters()[key].map((value, index) => (
            <option key={index} value={value}>{value}</option>
          ))}
        </select>
      </div>
    ))}
  </div>

  <table {...getTableProps()} id="dataTable" className={`${styles.huge_table} table-custom`}>
    <thead>
      {headerGroups.map(headerGroup => (
        <tr {...headerGroup.getHeaderGroupProps()}>
          {headerGroup.headers.map(column => (
            <th {...column.getHeaderProps()}>{column.render('Header')}</th>
          ))}
        </tr>
      ))}
    </thead>
    <tbody {...getTableBodyProps()}>
      {rows.map((row, i) => {
        prepareRow(row);
        return (
          <tr {...row.getRowProps()}>
            {row.cells.map(cell => {
              return <td {...cell.getCellProps()} onDoubleClick={(e) => handleDoubleClick(e, i, cell.column.accessor)}>{cell.render('Cell')}</td>;
            })}
          </tr>
        );
      })}
    </tbody>
  </table>
</>
);
}
