import React, { useState, useEffect } from 'react';
import { Container, Row, Col, Form, Button, Card } from 'react-bootstrap';
import { Document, Packer, Paragraph, Table, TableRow, TableCell, HeadingLevel, AlignmentType, WidthType } from 'docx';
import { saveAs } from 'file-saver';

const CreateScheduleProtectionForm = ({ groups_for_schedule, students_list_for_schedule }) => {
    const [selectedItems, setSelectedItems] = useState([]);
    const [dateStart, setDateStart] = useState('');
    const [audienceNumber, setAudienceNumber] = useState('');

    useEffect(() => {}, [groups_for_schedule]);

    const toggleAll = (event) => {
        const checkboxes = document.querySelectorAll('.nested-list input[type="checkbox"]');
        const isSelected = event.target.classList.contains('selected');
        checkboxes.forEach((checkbox) => {
            checkbox.checked = !isSelected;
        });
        event.target.classList.toggle('selected');
        updateSelectedItems();
    };

    const toggleSubList = (listId, event) => {
        const list = document.getElementById(listId);
        const toggleBtn = event.target;
        if (list.style.display === 'none') {
            list.style.display = 'block';
            toggleBtn.textContent = toggleBtn.textContent.replace('[+]', '[-]');
        } else {
            list.style.display = 'none';
            toggleBtn.textContent = toggleBtn.textContent.replace('[-]', '[+]');
        }
    };

    const updateSelectedItems = () => {
        const checkboxes = document.querySelectorAll('.nested-list input[type="checkbox"]:checked');
        const selected = [];
        checkboxes.forEach((checkbox) => {
            selected.push(checkbox.nextElementSibling.textContent);
        });
        setSelectedItems(selected);
    };

    const handleSubmit = (event) => {
        event.preventDefault();
        generateDocument();
    };

    if (!groups_for_schedule) {
        return <div>Loading...</div>;
    }

    const formatDate = (dateString) => {
        const date = new Date(dateString);
        const options = { day: 'numeric', month: 'long', year: 'numeric' };
        return date.toLocaleDateString('ru-RU', options);
    };

    const formattedDate = formatDate(dateStart);
    const bachelors = groups_for_schedule.filter(group => group.name.includes('Б'));
    const masters = groups_for_schedule.filter(group => group.name.includes('М'));

    const generateDocument = () => {
        console.log("selectedItems:", selectedItems);
        console.log("dateStart:", dateStart);
        console.log("audienceNumber:", audienceNumber);
    
        const doc = new Document({
            creator: "YourAppName",
            title: "Schedule Protection",
            description: "Document with the schedule protection details",
            sections: [],
            styles: {
                paragraphStyles: [
                    {
                        id: "default",
                        name: "Default",
                        basedOn: "Normal",
                        next: "Normal",
                        quickFormat: true,
                        run: {
                            font: "Times New Roman",
                            size: '12pt',
                            color: '000000'
                        },
                    },
                ],
            },
        });
    
        const documentTitle = new Paragraph({
            text: "Расписание защит кафедры СИИ ИКИТ СФУ",
            heading: HeadingLevel.HEADING_1,
            alignment: AlignmentType.CENTER,
            spacing: {
                before: 240,
                after: 240,
            },
            style: 'default'
        });
    
        const sectionProperties = {
            width: 15840,
            height: 10000,
            margins: {
                top: 1440,
                right: 1440,
                bottom: 1440,
                left: 1440,
            },
        };
    
        const combinedElements = [];
        combinedElements.push(documentTitle);
    
        selectedItems.forEach((groupName, index) => {
            const group = groups_for_schedule.find(group => group.name === groupName);
            console.log(group);
    
            // Находим студентов в текущей группе
            const studentsInGroup = students_list_for_schedule.filter(student => student.student.group.id === group.id);
            const numberOfStudents = studentsInGroup.length;
            console.log(`Группа: ${groupName}, Количество студентов: ${numberOfStudents}`);
    
            // Если количество студентов превышает 12, разбиваем группу на два дня
            if (numberOfStudents > 12) {
                const studentsPerDay = Math.ceil(numberOfStudents / 2);
                const firstDayStudents = studentsInGroup.slice(0, studentsPerDay);
                const secondDayStudents = studentsInGroup.slice(studentsPerDay);
    
                // Создаем таблицы для каждого дня
                const table1 = createTable(firstDayStudents, formattedDate, audienceNumber, groupName);
                const table2 = createTable(secondDayStudents, formattedDate, audienceNumber, groupName);
    
                combinedElements.push(table1);
                combinedElements.push(new Paragraph(""));
                combinedElements.push(table2);
            } else {
                // Если количество студентов меньше или равно 12, объединяем группы
                // до тех пор, пока не достигнем 12 студентов
                let currentStudents = [];
                let totalStudents = 0;
    
                studentsInGroup.forEach(student => {
                    // Проверяем, если добавление студента не превысит 12
                    if (totalStudents + 1 <= 12) {
                        currentStudents.push(student);
                        totalStudents++;
                    } else {
                        // Создаем таблицу с текущими студентами и добавляем ее в документ
                        const table = createTable(currentStudents, formattedDate, audienceNumber, groupName);
                        combinedElements.push(table);
                        combinedElements.push(new Paragraph(""));
    
                        // Сбрасываем текущих студентов и счетчик
                        currentStudents = [student];
                        totalStudents = 1;
                    }
                });
    
                // Создаем таблицу для оставшихся студентов
                if (currentStudents.length > 0) {
                    const table = createTable(currentStudents, formattedDate, audienceNumber, groupName);
                    combinedElements.push(table);
                    combinedElements.push(new Paragraph(""));
                }
            }
    
            // Продолжаем обработку остальных выбранных элементов
        });
    
        const section = {
            properties: sectionProperties,
            children: combinedElements,
        };
    
        doc.addSection(section);
    
        Packer.toBlob(doc).then(blob => {
            saveAs(blob, "schedule.docx");
        });
    };
    

    return (
        <Row className="justify-content-center">
            <Col md={7}>
                <h4 className="mb-4 text-center">Создание графика защит ВКР</h4>
                <Form className="px-4 py-2 m-2" onSubmit={handleSubmit}>
                    <Form.Group className="mb-3">
                        <Form.Label>Выберите нужных выпускников из списка:</Form.Label>
                        <ul>
                            <li className="top-level-item" onClick={toggleAll}>
                                Все <span className="toggle-btn">[+]</span>
                            </li>
                            <ul className="nested-list" id="all">
                                <li className="top-level-item" onClick={(e) => toggleSubList('bakalavry', e)}>
                                    Бакалавры <span className="toggle-btn">[+]</span>
                                </li>
                                <ul className="nested-list" id="bakalavry">
                                    {bachelors.map(group => (
                                        <li key={group.id}>
                                            <Form.Check 
                                                type="checkbox" 
                                                id={group.id} 
                                                label={group.name} 
                                                onClick={updateSelectedItems} 
                                            />
                                        </li>
                                    ))}
                                </ul>
                                <li className="top-level-item" onClick={(e) => toggleSubList('magistranty', e)}>
                                    Магистры <span className="toggle-btn">[+]</span>
                                </li>
                                <ul className="nested-list" id="magistranty">
                                    {masters.map(group => (
                                        <li key={group.id}>
                                            <Form.Check 
                                                type="checkbox" 
                                                id={group.id} 
                                                label={group.name} 
                                                onClick={updateSelectedItems} 
                                            />
                                        </li>
                                    ))}
                                </ul>
                            </ul>
                        </ul>
                        <div id="selected-items">
                            Выбранные элементы: {selectedItems.join(', ')}
                        </div>
                    </Form.Group>
                    <Form.Group className="mb-3">
                        <Form.Label>Введите дату начала защиты ВКР:</Form.Label>
                        <Form.Control type="date" value={dateStart} onChange={(e) => setDateStart(e.target.value)} />
                    </Form.Group>
                    
                    <Form.Group className="mb-3">
                        <Form.Label>Введите аудиторию:</Form.Label>
                        <Form.Control type="text" value={audienceNumber} onChange={(e) => setAudienceNumber(e.target.value)} />
                    </Form.Group>
                    <Button type="submit" className="button">
                        Рассчитать
                    </Button>
                </Form>
            </Col>
            <Col md={4} className="mt-4">
                <ol>
                    <li>Выберите нужную группу либо всех выпускников сразу</li>
                    <li>Далее введите планируемые даты начала и завершения защиты ВКР</li>
                    <li>Следующим шагом введите длительность временного интервала, рассчитанного на одно выступление выпускника.</li>
                    <li>Нажмите “Рассчитать”.</li>
                    <li>
                        Далее вы сможете сохранить полученный список как черновик, опубликовать (тогда его увидят все пользователи системы) или экспортировать в формат word
                    </li>
                </ol>
            </Col>
        </Row>
    );
};

export default CreateScheduleProtectionForm;
