import React from 'react';
import { Table } from 'react-bootstrap';
import styles from './Protection.module.css';

const ViewProtectionSchedulePersons = ({ protection_schedule, students_list_for_schedule }) => {

  const getFullName = (studentData) => {
    if (studentData && studentData.user) {
      const { last_name, first_name, patronymic } = studentData.user;
      return `${last_name} ${first_name} ${patronymic}`;
    } else {
      return 'Unknown';
    }
  };

  const distributeStudentsByProtectionDays = (schedule) => {
    const groupedSchedule = {};

    const usedStudents = new Set();

    schedule.forEach((item) => {
      if (!item.group) return;

      const dateKey = item.date;
      const groupName = item.group.name;
      const groupId = item.group.id;

      if (!groupedSchedule[dateKey]) {
        groupedSchedule[dateKey] = {};
      }

      if (!groupedSchedule[dateKey][groupName]) {
        groupedSchedule[dateKey][groupName] = { day1: [], day2: [] };
      }

      const currentGroup = groupedSchedule[dateKey][groupName];
      const students = students_list_for_schedule.filter((student) => student.group.id === groupId);

      // Determine which day to add students to
      const dayToAdd = item.date === new Date()? 'day1' : 'day2';

      // Exclude students that have already been added on previous days
      const availableStudents = students.filter((student) => !usedStudents.has(student.user.username));

      // Take the first 12 students or fewer if there are less
      const studentsToAdd = availableStudents.slice(0, 12);

      // Add students to the respective day
      currentGroup[dayToAdd].push(...studentsToAdd);

      // Add used students to the set
      studentsToAdd.forEach((student) => usedStudents.add(student.user.username));
    });

    return groupedSchedule;
  };

  const groupedSchedule = distributeStudentsByProtectionDays(protection_schedule);

  return (
    <div>
      <h4 className='text-center'> График защит ВКР по фамильный</h4>
      <div className='mx-md-5'>
        {Object.entries(groupedSchedule).map(([date, groups]) => (
          <div key={date}>
            <h3>{date}</h3>
            {Object.entries(groups).map(([group, days]) => (
              <React.Fragment key={group}>
                {days.day1.length > 0 && days.day2.length > 0 && (
                  <React.Fragment>
                    <Table bordered hover responsive className={styles.protectionTable}>
                      <thead>
                        <tr>
                          <th>ФИО</th>
                          <th>Группа</th>
                          <th>Дата</th>
                        </tr>
                      </thead>
                      <tbody>
                        {days.day1.map((student, index) => (
                          <tr key={index}>
                            <td>{getFullName(student)}</td>
                            <td>{group}</td>
                            <td>{date}</td>
                          </tr>
                        ))}
                      </tbody>
                    </Table>

                    <Table bordered hover responsive className={styles.protectionTable}>
                      <thead>
                        <tr>
                          <th>ФИО</th>
                          <th>Группа</th>
                          <th>Дата</th>
                        </tr>
                      </thead>
                      <tbody>
                        {days.day2.map((student, index) => (
                          <tr key={index}>
                            <td>{getFullName(student)}</td>
                            <td>{group}</td>
                            <td>{date}</td>
                          </tr>
                        ))}
                      </tbody>
                    </Table>
                  </React.Fragment>
                )}
                {days.day1.length > 0 && days.day2.length === 0 && (
                  <Table bordered hover responsive className={styles.protectionTable}>
                    <thead>
                      <tr>
                        <th>ФИО</th>
                        <th>Группа</th>
                        <th>Дата</th>
                      </tr>
                    </thead>
                    <tbody>
                      {days.day1.map((student, index) => (
                        <tr key={index}>
                          <td>{getFullName(student)}</td>
                          <td>{group}</td>
                          <td>{date}</td>
                        </tr>
                      ))}
                    </tbody>
                  </Table>
                )}
                {days.day1.length === 0 && days.day2.length > 0 && (
                  <Table bordered hover responsive className={styles.protectionTable}>
                    <thead>
                      <tr>
                        <th>ФИО</th>
                        <th>Группа</th>
                        <th>Дата</th>
                      </tr>
                    </thead>
                    <tbody>
                      {days.day2.map((student, index) => (
                        <tr key={index}>
                          <td>{getFullName(student)}</td>
                          <td>{group}</td>
                          <td>{date}</td>
                        </tr>
                      ))}
                    </tbody>
                  </Table>
                )}
              </React.Fragment>
            ))}
          </div>
        ))}
      </div>
    </div>
  );
};

export default ViewProtectionSchedulePersons;
