import React, { Component } from 'react'
import BaseModal from '../BaseModal/BaseModal'
import { Modal, Table } from 'react-bootstrap';
import styles from './ScheduleFQWModal.module.css'

export default class ScheduleFQWModal extends Component {
    
  render() {
    const { show, handleClose, darkMode, schedule_stud_prof } = this.props;
    console.log (schedule_stud_prof)
    const content = (
        <Table striped bordered hover>
        <thead>
          <tr>
            <th>Этап</th>
            <th>Начало</th>
            <th>Конец</th>
            <th>Результат</th>
            <th>Отметка о выполнении</th>
            <th>Пропуск</th>
          </tr>
        </thead>
        <tbody>
          {schedule_stud_prof.map(stage => (
            <tr key={stage.id}>
              <td>{stage.stage_name}</td>
              <td>{stage.start}</td>
              <td>{stage.end}</td>
              <td>{stage.result}</td>
              <td>{stage.completion_mark}</td>
              <td>{stage.absence}</td>
            </tr>
          ))}
        </tbody>
      </Table>

    );
    return (
      <BaseModal show={this.props.show} 
      handleClose={this.props.handleClose} 
      title={'График работы над ВКР'}
      content={content} 
      darkMode={darkMode}
      sourceData={schedule_stud_prof}
      style={styles.container_modal}
      >
        
        </BaseModal>
    )
  }
}
