import React, { Component, useState } from 'react'
import styles from './MethodRecommendationsCard.module.css';
import { Tabs, Tab, Row, Col } from 'react-bootstrap';

const MethodRecommendationsCard = () => {

    const [activeTab, setActiveTab] = useState('tab1');

    const handleTabSelect = (tabId) => {
      setActiveTab(tabId);
    };

    return (
      <div className={styles.container}>
        <div className={styles.card}>
         <Row>
          <Col md={11}>
            <Tabs activeKey={activeTab} onSelect={handleTabSelect}>
            <h4 class="text-center">Общие требования к оформлению выпускной квалификационной работы </h4>
            <p class="mt-3">Пояснительная записка (ПЗ) выполняется печатным способом. При  оформлении необходимо пользоваться нормативом СТУ 7.5–07-2021.
Рекомендуется: текстовый процессор – MicrosoftWord, шрифт TimesNewRoman, 14 пунктов, межстрочный интервал 1 или 1,5. Абзацный отступ должен быть одинаковым по всему тексту и равный пяти знакам
или 12,5 мм. Текст ПЗ печатают на листах без рамки с соблюдением размеров полей: </p>
              <Tab eventKey="tab1" title="Оформление">
                <Row>
                  <Col md={6}>
                    <h6>при вертикальной ориентации:</h6>
                    {/* Здесь содержимое для первой вкладки */}
                  </Col>
                  <Col md={6}>
                    <h6>при горизонтальной ориентации:</h6>
                    {/* Здесь содержимое для первой вкладки */}
                  </Col>
                </Row>
              </Tab>
              <Tab eventKey="tab2" title="Содержание">
                {/* Здесь содержимое для второй вкладки */}
              </Tab>
              <Tab eventKey="tab3" title="Графическая часть">
                {/* Здесь содержимое для третьей вкладки */}
              </Tab>
            </Tabs>
          </Col>
          <Col md={1}>
            {/* Содержимое для правой части, если необходимо */}
          </Col>
        </Row>
        </div>
      </div>
    );
  };
  
  export default MethodRecommendationsCard;
