import petrovich from 'petrovich';
import RussianNouns from 'russian-nouns-js';
import { saveAs } from 'file-saver';
import PizZip from 'pizzip';
import Docxtemplater from 'docxtemplater';
import word from './1.docx'; 

const generateTask = async ({request_data_task }) => {
  
  const fullname = `${studentData.last_name} ${studentData.first_name} ${studentData.patronymic}`;
  const surname_gen = petrovich.female.last.genitive(studentData.last_name);
  
  const rne = new RussianNouns.Engine();
  const declineWord = (word, gender) => rne.decline({ text: word.split(' ')[0], gender }, 'родительный');
  let form_of_studying = studentData.form_of_studying.replace(studentData.form_of_studying.split(' ')[0], declineWord(studentData.form_of_studying, 'женский'));
  let academic_title = teacherData.academic_title.replace(teacherData.academic_title.split(' ')[0], declineWord(teacherData.academic_title, 'средний'));
  let job_title = teacherData.job_title.replace(teacherData.job_title.split(' ')[0], declineWord(teacherData.job_title, 'средний'));
  let place_of_work = teacherData.place_of_work.replace(teacherData.place_of_work.split(' ')[0], declineWord(teacherData.place_of_work, 'женский'));

  try {

    const response = await fetch(word);
    if (!response.ok) throw new Error('Ошибка сети');
    const arrayBuffer = await response.arrayBuffer();
    const zip = new PizZip(arrayBuffer);
    const doc = new Docxtemplater(zip);

    const dataToRender = {
      last_name: studentData.last_name,
      first_name: studentData.first_name,
      patronymic: studentData.patronymic,
      group_name: studentData.group.name,
      speciality_code: studentData.speciality.code,
      speciality_name: studentData.speciality.name,
      theme_name: requestData.theme.name,
      np: teacherData.np,
      first_name_teacher: teacherData.first_name,
      job_title: job_title,
      academic_title: academic_title,
      place_of_work: place_of_work,
    };

    // Установка данных и рендеринг документа
    doc.setData(dataToRender);
    doc.render();

    // Сохранение документа
    const output = doc.getZip().generate({ type: 'blob' });
    saveAs(output, 'Задание на ВКР.docx');
  } catch (error) {
    console.error('Ошибка загрузки файла:', error);
  }
};

export default generateTask;
