import React, { useState } from 'react';
import styles from './SupervisedStudents.module.css';
import { Row, Col, Button } from 'react-bootstrap';
import ModalViewGraphExists from '../Modals/ModalViewGraphExists/ModalViewGraphExists'; // Импорт вашего модального окна
import ModalConfirm from '../Modals/ModalConfirm/ModalConfirm'; // Импорт модального окна подтверждения

function SupervisedStudentsCard({ teacherData, requestData, work_schedule, work_protection }) {
  const [activeRequest, setActiveRequest] = useState(null);
  const [showModal, setShowModal] = useState(false);
  const [modalData, setModalData] = useState({});
  const [showConfirmationModal, setShowConfirmationModal] = useState(false); // Добавлено состояние для модального окна подтверждения

  const handleToggleActions = (requestId) => {
    setActiveRequest(activeRequest === requestId ? null : requestId);
  };

  const openModalViewTeacherGraph = (request) => {
    setModalData({
      schedule_stud_prof: work_schedule.filter(sch => sch.id_request === request.id),
      request_data_task: request, 
      teacherData: teacherData
    });
    setShowModal(true);
  };

  const handleCloseModal = () => {
    setShowModal(false);
  };

  const handleCancelSupervision = () => {
    // Show the confirmation modal when the "Отказаться от курирования" button is clicked
    setShowConfirmationModal(true);
  };

  const handleCloseConfirmationModal = () => {
    setShowConfirmationModal(false);
  };

  return (
    <>
      <h4 className="mb-4 text-center">Курируемые выпускники</h4>
      <div className={styles.block}>
        {requestData.map((request) => {
          if (request.is_accepted_request === true) {
            const protection = work_protection.find(wp => wp.id_request === request.id);
            const schedules = work_schedule.filter(sch => sch.id_request === request.id);
            const today = new Date();

            const isWithinDateRange = (start, end) => {
              const startDate = new Date(start);
              const endDate = new Date(end);
              return today >= startDate && today <= endDate;
            };

            const activeSchedule = schedules.find(schedule => 
              schedule.start && schedule.end && isWithinDateRange(schedule.start, schedule.end)
            );

            return (
              <div key={request.id}>
                <Row className='justify-content-center'>
                  <Col md={3}>{request.student.surname} {request.student.name} {request.student.patronymic}</Col>
                  <Col md={6}>{request.student.group}</Col>
                  <Col md={3}>
                    <Button 
                      variant='none' 
                      className={styles.button_actions}
                      onClick={() => handleToggleActions(request.id)}
                    >
                      Действия
                    </Button>
                    {activeRequest === request.id && (
                      <div className={styles.actions_list}>
                        <button onClick={() => openModalViewTeacherGraph(request)}>График работы над ВКР</button> 
                        <button onClick={handleCancelSupervision}>Отказаться от курирования</button> {/* Added onClick event */}
                      </div>
                    )}
                  </Col>
                </Row>
                {/* Remaining code */}
              </div>
            );
          } else {
            return null; 
          }
        })}
      </div>
      {showModal && (
        <ModalViewGraphExists 
          show={showModal} 
          handleClose={handleCloseModal} 
          darkMode={false} 
          schedule_stud_prof={modalData.schedule_stud_prof} 
          request_data_task={modalData.request_data_task} 
          teacherData={teacherData}
        />
      )}
      {/* Render the confirmation modal when showConfirmationModal is true */}
      {showConfirmationModal && (
        <ModalConfirm
          show={showConfirmationModal}
          handleClose={handleCloseConfirmationModal}
          darkMode={false}
        />
      )}
    </>
  );
}

export default SupervisedStudentsCard;
