import React, { useState } from 'react';
import { Container, Row, Col, Form, Button } from 'react-bootstrap';
import styles from '../../ProfileBlocks.module.css';
import reveal from '../../../../img/reveal.png'
import ModalRequests from '../../../Modals/ModalRequests/ModalRequests';


const RequestsCard = ({ requestdata }) => {
  const [currentRequestIndex, setCurrentRequestIndex] = useState(0);
  const [requestsCount, setRequestsCount] = useState(requestdata.length);
  const handleAccept = () => {
    setCurrentRequestIndex(prevIndex => prevIndex + 1);
    setRequestsCount(prevCount => prevCount - 1);
  };

  const handleDecline = () => {
    setCurrentRequestIndex(prevIndex => prevIndex + 1);
    setRequestsCount(prevCount => prevCount - 1);
  };

  const currentRequest = requestdata[currentRequestIndex];
  const noRequestsLeft = !currentRequest || currentRequestIndex >= requestdata.length;
  const [showModal, setShowModal] = useState(false);
  const visibleRequests = requests.filter(request => !request.hidden);
  return (
    <div className={styles.card}>
      
      <p className="text-center px-1 py-2 fw-bold">Текущие заявки <span className={styles.badge}>{requestsCount}</span>
       <img src={reveal} class="mx-3" width="20" height="20" onClick={() => setShowModal(true)}/> </p>
      {noRequestsLeft ? (
        <p className="text-center">Вам еще не были отправлены заявки</p> 
      ) : (
        <div className={styles.request_field}>
          <Row>
            <Col md={7} xs={12}>{currentRequest.name}</Col>
            <Col md={3} xs={4}>{currentRequest.group}</Col>
            <Col md={2} xs={3}>{currentRequest.rating}</Col>
          </Row>
          <Row>
            <Col md={4} xs={4} className="font-weight-bold">Тема ВКР:</Col>
            <Col md={8} xs={8}>{currentRequest.theme_str}</Col>
          </Row>
          <Row>
            <Col md={4} xs={4}>Тип ВКР:</Col>
            <Col md={8} xs={8}>{currentRequest.type}</Col>
          </Row>
          <Row>
            <Col md={4} xs={12}>Дополнительно:</Col>
            <Col md={8} xs={12}>{currentRequest.additional}</Col>
          </Row>
          <Row>
            <Col md={1} xs={2}>
              <Form.Check type="checkbox" id={currentRequest.id} />
            </Col>
            <Form.Label htmlFor={currentRequest.id} className="col-md-11 col-10">
              Утвердить тему ВКР
            </Form.Label>
          </Row>
          <Row className="justify-content-center">
            <Col md={5} xs={5}>
              <Button variant='none' className={styles.button_accept} onClick={handleAccept}>Принять заявку</Button>
            </Col>
            <Col md={5} xs={5}>
              <Button variant='none' className={styles.button_decline} onClick={handleDecline}>Отклонить заявку</Button>
            </Col>
          </Row>
        </div>
      )}
       <ModalRequests
        show={showModal}
        onHide={() => setShowModal(false)}
        requestdata={requestdata}
        sourceData={visibleRequests}
      />
   </div>
  );
}

export default RequestsCard;
