import React, { useState } from 'react';
import { Table, Col, Row, Button } from 'react-bootstrap';

function ThemesListReading({ data, themes_list }) {
  const [filter, setFilter] = useState('');

  const filteredThemes = themes_list.filter((theme) => {
    // Если фильтр не указан, показываем все темы
    if (!filter) {
      return true;
    }

    // Проверяем, содержит ли ФИО преподавателя или строку "кафедра" из фильтра
    const teacherName = theme.teacher.user.id !== null ?
      `${theme.teacher.user.last_name} ${theme.teacher.user.first_name} ${theme.teacher.user.patronymic}` :
      'кафедра';

    return teacherName.toLowerCase().includes(filter.toLowerCase());
  });

  return (
    <div>
      <Row className='text-center'>
        <Col md={9}>
          <h4 className='text-center'>Список тем</h4>
        </Col>
        {(data.type_of_user === 'руководитель' || data.type_of_user === 'секретарь ГЭК') && (
          <Col md={2}>
            <Button>Редактировать</Button>
          </Col>
        )}
      </Row>

      <div className='mx-2 mt-4'>
        <input
          type='text'
          className='form-control mb-3'
          placeholder='Фильтр по ФИО преподавателя или кафедре'
          value={filter}
          onChange={(e) => setFilter(e.target.value)}
        />
       <Table bordered hover>
  <thead>
    <tr>
      <th>ID</th>
      <th style={{ width: '20%' }}>Название</th> {/* Устанавливаем ширину столбца с темами */}
      <th >Преподаватель</th>
      <th style={{ width: '20%' }}>Организация</th> {/* Устанавливаем ширину столбца с организацией */}
      <th style={{ width: '13%' }}>Рецензент</th>
      <th>ВКР по заявке</th>
      <th>Свободна</th>
    </tr>
  </thead>
  <tbody>
    {filteredThemes.map((theme) => (
      <tr key={theme.id}>
        <td>{theme.id}</td>
        <td>{theme.name}</td>
        <td>
          {theme.teacher.user.id !== null ?
            `${theme.teacher.user.last_name} ${theme.teacher.user.first_name} ${theme.teacher.user.patronymic}` :
            theme.by_student ? 'Предложена студентом' : 'Кафедра СИИ'
          }
        </td>
        <td>
          {theme.company.name && (
            <>
              <div>Название: {theme.company.name}</div>
              <div>
                Ответственный: {theme.company.last_name_of_responsible}{' '}
                {theme.company.first_name_of_responsible} {theme.company.patronymic_of_responsible}
              </div>
              <div>Должность: {theme.company.job_title_of_resposible}</div>
              <div>Дополнительная информация: {theme.company.additional_information}</div>
            </>
          )}
        </td>
        <td>
          {theme.reviewer.user.id !== null ?
            `${theme.reviewer.user.last_name} ${theme.reviewer.user.first_name} ${theme.reviewer.user.patronymic}` :
            'Не назначен'
          }
        </td>
        <td>{theme.fqw_by_application}</td>
        <td>{theme.free ? 'Да' : 'Нет'}</td>
      </tr>
    ))}
  </tbody>
</Table>

      </div>
    </div>
  );
}

export default ThemesListReading;
