// Footer.js
import React, { useState } from 'react';
import { Row, Col } from 'react-bootstrap';
import ikit from './ikit.png';
import styles from './Footer.module.css';
import AboutSystemModal from './AboutSystemModal'; // Импорт компонента модального окна

const Footer = () => {
  const [showAboutSystemModal, setShowAboutSystemModal] = useState(false);

  const handleAboutSystemModalClose = () => setShowAboutSystemModal(false);
  const handleAboutSystemModalShow = () => setShowAboutSystemModal(true);

  return (
    <footer className={`footer ${styles.footer} p-md-5`}>
      <Row className="ml-4">
        <Col md={4} xs={12} className="text-center mb-3 mb-md-0">
          <a href="https://ikit.sfu-kras.ru/">
            <img src={ikit} className="ikit" width="70%" alt="Light Icon" loading="lazy" />
          </a>
        </Col>
        <Col md={8} xs={12}>
          <Row>
            <Col md={6} xs={12} className="text-left">
              <a href="#" style={{ fontWeight: 'bold' }} id="privacy_policy">Политика конфиденциальности</a>
            </Col>
            <Col md={6} xs={12} className="text-left">
              <a href="https://about.sfu-kras.ru/node/10201" style={{ fontWeight: 'bold' }}>Соглашение о персональных данных</a>
            </Col>
          </Row>
          <Row className="mt-md-2">
            <Col md={6} xs={12} className="text-left">
              <a href="#" style={{ fontWeight: 'bold' }} id="system_instructions">Инструкции по работе в системе</a>
            </Col>
            <Col md={6} xs={12} className="text-left">
              <a href="#" style={{ fontWeight: 'bold' }} onClick={handleAboutSystemModalShow}>О системе</a>
            </Col>
          </Row>
        </Col>
      </Row>

      {/* Использование компонента AboutSystemModal */}
      <AboutSystemModal show={showAboutSystemModal} handleClose={handleAboutSystemModalClose} />
    </footer>
  );
};

export default Footer;
