  import React, { useState, useEffect } from 'react';
  import { Form, Button, Row, Col } from 'react-bootstrap';
  import plus from '../../../img/plus.png';
  import del from '../../../img/del.png';
  import edit from '../../../img/edit.png';
  import styles from '../EditThemeList/EditThemeList.module.css';

  const EditThemeListTeacherForm = ({ themesList: initialThemesList, data }) => {
    const [themesList, setThemesList] = useState(initialThemesList || []);
    const [editingIndex, setEditingIndex] = useState(null);
    const [editingData, setEditingData] = useState(null);
    const [editFormVisibleIndex, setEditFormVisibleIndex] = useState(null);
    const [isLinked, setIsLinked] = useState(false);

    const addRow = () => {
      const newRow = {
        name: '',
        teacher: {},
        company: { name: '', last_name_of_responsible: '', first_name_of_responsible: '', job_title_of_responsible: '', additional_information: '' },
        free: null,
      };
      const updatedThemesList = [newRow, ...themesList];
      setThemesList(updatedThemesList);
      setEditFormVisibleIndex(0);
      setEditingIndex(0);
      setEditingData(newRow);
    };

    const handleEditClick = (index) => {
      setEditingIndex(index);
      setEditingData(themesList[index]);
      setEditFormVisibleIndex(index);
      setIsLinked(!!themesList[index].company.name);
    };

    const handleCancel = () => {
      setEditFormVisibleIndex(null);
    };

    const handleSave = (updatedData) => {
      const updatedThemesList = themesList.map((item, index) =>
        index === editingIndex ? updatedData : item
      );
      setThemesList(updatedThemesList);
      setEditFormVisibleIndex(null);
      setEditingIndex(null);
    };

    const handleDeleteClick = (index) => {
      const newData = [...themesList];
      newData.splice(index, 1);
      setThemesList(newData);

      if (editFormVisibleIndex === index) {
        setEditFormVisibleIndex(null);
      }
      setEditingIndex(null);
    };

    const handleInputChange = (e) => {
      const { name, value, type, checked } = e.target;
      setEditingData({
        ...editingData,
        [name]: type === 'checkbox' ? checked : value,
      });
    };

    const handleCompanyChange = (e) => {
      const { name, value } = e.target;
      setEditingData({
        ...editingData,
        company: {
          ...editingData.company,
          [name]: value,
        },
      });
    };

    const filteredThemesList = themesList.filter(item => 
      (item.teacher && item.teacher.user && item.teacher.user.id === data.id) || 
      item.name === ''
    );

    return (
      <>
        <h4 className="mb-4 text-center">Редактирование списка тем ВКР</h4>

        <table width="100%" className={styles.table_themes}>
          <thead className="text-center">
            <tr>
              <th width="60%" rowSpan="2">Наименование темы ВКР</th>
              <th width="25%" colSpan="4" className="no-filter">Организация</th>
              <th rowSpan="2">Занята</th>
              <th rowSpan="2" className="no-filter">Действия</th>
              <th rowSpan="2" className="no-filter">
                <img id="addRowButton" src={plus} onClick={addRow} width="30" height="30" alt="Add Row" />
              </th>
            </tr>
            <tr>
              <th>Наименование</th>
              <th>Ответственный</th>
              <th>Должность</th>
              <th>Дополнительно</th>
            </tr>
          </thead>
          <tbody>
            {filteredThemesList.map((item, index) => (
              <React.Fragment key={index}>
                <tr>
                  <td>{item.name}</td>
                  <td>{item.company ? item.company.name : ''}</td>
                  <td>{`${item.company ? item.company.last_name_of_responsible : ''} ${item.company ? item.company.first_name_of_responsible : ''} ${item.company ? item.company.patronymic_of_responsible : ''}`}</td>
                  <td>{item.company ? item.company.job_title_of_responsible : ''}</td>
                  <td>{item.company ? item.company.additional_information : ''}</td>
                  <td>{item.free ? 'Свободна' : 'Занята'}</td>
                  <td>
                    <img key="edit" className="action-button" src={edit} onClick={() => handleEditClick(index)} width="20" height="20" alt="edit" />
                    <img key="delete" className="action-button" src={del} onClick={() => handleDeleteClick(index)} width="20" height="20" alt="delete" />
                  </td>
                </tr>
                {editFormVisibleIndex === index && (
                  <tr>
                    <td colSpan="7">
                      <Form className="py-2 my-2 mx-5">
                        <Form.Group as={Row}>
                          <Form.Label column sm={4}>Тема ВКР</Form.Label>
                          <Col sm={8}>
                            <Form.Control
                              type="text"
                              name="name"
                              value={editingData.name}
                              onChange={handleInputChange}
                              placeholder="тема ВКР"
                            />
                          </Col>
                        </Form.Group>
                        <Form.Group as={Row} controlId="organization_link">
                          <Form.Label column sm={4}>Связь темы ВКР с организацией</Form.Label>
                          <Col sm={8} className="d-flex align-items-center">
                            <Form.Check 
                              type="radio" 
                              name="have_organization" 
                              value="no" 
                              label="Не связана" 
                              className="mr-2" 
                              checked={!isLinked}
                              onChange={() => setIsLinked(false)}
                            />
                            <Form.Check 
                              type="radio" 
                              name="have_organization" 
                              value="yes" 
                              label="Связана" 
                              className="ml-2" 
                              checked={isLinked}
                              onChange={() => setIsLinked(true)}
                            />
                          </Col>
                        </Form.Group>
                        {isLinked && (
                          <>
                            <Form.Group as={Row} controlId="organization_name_field">
                              <Form.Label column sm={4}>Наименование организации</Form.Label>
                              <Col sm={8}>
                                <Form.Control
                                  type="text"
                                  name="name"
                                  value={editingData.company.name}
                                  onChange={handleCompanyChange}
                                  className={styles.form_control}
                                  placeholder="Введите наименование организации"
                                />
                              </Col>
                            </Form.Group>
                            <Form.Group as={Row} controlId="fio_responsible_field">
                              <Form.Label column sm={4}>ФИО ответственного</Form.Label>
                              <Col sm={8}>
                                <Form.Control
                                  type="text"
                                  name="last_name_of_responsible"
                                  value={editingData.company.last_name_of_responsible}
                                  onChange={handleCompanyChange}
                                  className={styles.form_control}
                                  placeholder="ФИО ответственного"
                                />
                              </Col>
                            </Form.Group>
                            <Form.Group as={Row} controlId="post_responsible_field">
                              <Form.Label column sm={4}>Должность ответственного</Form.Label>
                              <Col sm={8}>
                                <Form.Control
                                  type="text"
                                  name="job_title_of_responsible"
                                  value={editingData.company.job_title_of_responsible}
                                  onChange={handleCompanyChange}
                                  className={styles.form_control}
                                  placeholder="Должность ответственного"
                                />
                              </Col>
                            </Form.Group>
                            <Form.Group as={Row} controlId="additional_responsible">
                              <Form.Label column sm={4}>Дополнительно</Form.Label>
                              <Col sm={8}>
                                <Form.Control
                                  type="text"
                                  name="additional_information"
                                  value={editingData.company.additional_information}
                                  onChange={handleCompanyChange}
                                  className={styles.form_control}
                                  placeholder="Дополнительная информация"
                                />
                              </Col>
                            </Form.Group>
                          </>
                        )}
                        <Form.Group as={Row} controlId="isFree">
                          <Form.Label column sm={4}>Свободна или нет</Form.Label>
                          <Col sm={8}>
                            <Form.Check
                              type="checkbox"
                              name="free"
                              checked={editingData.free}
                              onChange={handleInputChange}
                              className={styles.form_control}
                            />
                          </Col>
                        </Form.Group>
                        <Row className="justify-content-center">
                          <Col md={4}>
                            <Button variant="secondary" onClick={handleCancel}>Отмена</Button>
                          </Col>
                          <Col md={4}>
                            <Button variant="primary" onClick={() => handleSave(editingData)}>Сохранить изменения</Button>
                          </Col>
                        </Row>
                      </Form>
                    </td>
                  </tr>
                )}
              </React.Fragment>
            ))}
          </tbody>
        </table>
      </>
    );
  }

  export default EditThemeListTeacherForm;
