import React, { Component } from 'react';
import { Container, Row, Col, Form, Button, Modal } from 'react-bootstrap';
import styles from "../RequestCard/RequestCard.module.css";
import styles_base from "../../Forms/FormsBase.module.css";

export default class ChooseNewTheme extends Component {
  constructor(props) {
    super(props);
    this.state = {
      showModal: false,
      haveOrganization: false,
      isRequestCreated: false,
      selectedTheme: null,
      type_vkr: '',
    };
  }

  componentDidMount() {
    // Проверка существования объекта
    this.checkRequestExistence();
  }

  checkRequestExistence = async () => {
    try {
      const response = await fetch('/api/requests');
      if (response.ok) {
        const data = await response.json();
        if (data.exists) {
          this.setState({ isRequestCreated: true });
        }
      }
    } catch (error) {
      console.error('Ошибка при проверке существования заявки:', error);
    }
  };

  createRequest = async (event) => {
    event.preventDefault();
    const { selectedTheme, type_vkr } = this.state;
    const { studentData } = this.props;

    const requestData = {
      student: {
        user: {
          id: studentData.user.id,
        },
      },
      consultant: {
        user: {
          id: studentData.user.id,
        },
      },
      id_theme: selectedTheme.id,
      theme_str: selectedTheme.name,
      is_accepted_theme: false,
      is_accepted_request: false,
      type_of_fwq: type_vkr,
    };

    try {
      const response = await fetch('/api/requests', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(requestData),
      });

      if (response.ok) {
        this.setState({ isRequestCreated: true });
        alert('Заявка успешно создана');
      } else {
        alert('Ошибка при создании заявки');
      }
    } catch (error) {
      console.error('Ошибка при создании заявки:', error);
      alert('Ошибка при создании заявки');
    }
  };

  toggleModal = () => {
    this.setState(prevState => ({
      showModal: !prevState.showModal
    }));
  };

  toggleOrganization = () => {
    this.setState(prevState => ({
      haveOrganization: !prevState.haveOrganization
    }));
  };

  handleThemeSelect = (selectedTheme) => {
    this.setState({ selectedTheme });
  };

  handleTypeChange = (event) => {
    this.setState({ type_vkr: event.target.value });
  };

  render() {
    const { themes_list, studentData } = this.props;
    const { isRequestCreated, showModal, selectedTheme, haveOrganization } = this.state;

    return (
      <>
        <Row className="justify-content-center">
          <Col md={10} className="px-4">
            <Row className='text-center mt-2'>
              <Col md={9}>
                <h4 className="mb-4 text-center">Подача заявки на курирование ВКР</h4>
              </Col>
              <Col md={3}>
                <Button variant='none' className={styles.button1} onClick={this.toggleModal}>Правила</Button>
              </Col>
            </Row>

            <Modal show={showModal} onHide={this.toggleModal}>
              <Modal.Header closeButton>
                <Modal.Title>Правила заполнения формы</Modal.Title>
              </Modal.Header>
              <Modal.Body>
                <ol>
                  <li>Нельзя отправить новую заявку, если уже есть текущая или подтвержденная заявка. Ее придется удалить.</li>
                  <li>Количество отправлений заявки ограничено. Нельзя отправить заявку более трех раз. Если вы столкнулись с превышением количества возможных отправлений - обратитесь в техническую поддержку.</li>
                  <li>При формировании заявки вы можете указать тему либо оставить ее пустой. После того как вашу заявку примет руководитель, вам нужно будет согласовать тему.</li>
                  <li>Если предлагаете тему, связанную с организацией, укажите название организации и данные ответственного.</li>
                  <li>Если потенциальный руководитель отклонил вашу заявку, попробуйте подать новую другому.</li>
                </ol>
              </Modal.Body>
              <Modal.Footer>
                <Button variant="secondary" onClick={this.toggleModal}>Закрыть</Button>
              </Modal.Footer>
            </Modal>

            {isRequestCreated ? (
              <div>Заявка уже создана</div>
            ) : (
              <Form className="py-2 my-2" onSubmit={this.createRequest}>
                <ChooseTheme themes_list={themes_list} onThemeSelect={this.handleThemeSelect} />

                <Form.Group as={Row} controlId="type_vkr">
                  <Form.Label column sm={4}>Вид ВКР</Form.Label>
                  <Col sm={8}>
                    <Form.Control as="select" className={styles_base.form_control} onChange={this.handleTypeChange}>
                      <option value="академическое">академическое</option>
                      <option value="проектно-исследовательское">проектно-исследовательское</option>
                      <option value="комплексное">комплексное</option>
                      <option value="в формате стартапа">в формате стартапа</option>
                      <option value="в формате стартапа с актом внедрения">в формате стартапа с актом внедрения</option>
                    </Form.Control>
                  </Col>
                </Form.Group>

                <Form.Group as={Row} controlId="additional_info">
                  <Form.Label column sm={4}>Дополнительно:</Form.Label>
                  <Col sm={8}>
                    <Form.Control as="textarea" className={styles_base.form_control} rows={3} placeholder="Дополнительная информация" />
                  </Col>
                </Form.Group>

                <Row className='justify-content-center'>
                  <Col md={6}><Button type="submit" className={styles_base.button}>Отправить</Button></Col>
                </Row>
              </Form>
            )}
          </Col>
        </Row>
      </>
    );
  }
}

class ChooseTheme extends Component {
  constructor(props) {
    super(props);
    this.state = {
      selectedOption: '',
      haveOrganization: false,
      selectedTheme: null,
    };
  }

  handleSelectChange = (option) => {
    this.setState({ selectedOption: option, selectedTheme: null });
  };

  handleThemeSelect = (themeId) => {
    const theme = this.props.themes_list.find(theme => theme.id === themeId);
    this.setState({ selectedTheme: theme });
    this.props.onThemeSelect(theme);
  };

  render() {
    const { themes_list } = this.props;
    const { selectedOption, haveOrganization, selectedTheme } = this.state;

    const filteredDepartmentThemes = themes_list.filter(theme => theme.department);
    const filteredTeacherThemes = themes_list.filter(theme => theme.free === true);

    return (
      <div>
        <div className={`${styles.button_group} my-4`} >
          <Button variant="ligth" className={styles.button1} onClick={() => this.handleSelectChange('select1')}>
            Предложить свою тему
          </Button>
          <Button variant="light" className={styles.button2} onClick={() => this.handleSelectChange('select2')}>
            Выбрать из перечня тем кафедры
          </Button>
          <Button variant="light" className={styles.button3} onClick={() => this.handleSelectChange('select3')}>
            Выбрать из тем, предложенных руководителем
          </Button>
        </div>
        <div className="select-group my-4">
          {selectedOption === 'select1' && (
            <Form>
              <Form.Group as={Row} controlId="formPlaintextEmail">
                <Form.Label column sm="4">
                  Своя тема ВКР
                </Form.Label>
                <Col sm="8">
                  <Form.Control type="text" className={styles_base.form_control} placeholder="тема ВКР" />
                </Col>
              </Form.Group>
            </Form>
          )}
          {selectedOption === 'select2' && (
            <div>
              <Form.Group as={Row}>
                <Form.Label htmlFor="list_themes_department" column sm={4}>Перечень тем кафедры</Form.Label>
                <Col sm={8}>
                  <Form.Control as="select" className={styles_base.form_control} id="list_themes_department" onChange={(e) => this.handleThemeSelect(parseInt(e.target.value))}>
                    <option value={0}>-</option>
                    {filteredDepartmentThemes.map(theme => (
                      <option key={theme.id} value={theme.id}>{theme.name}</option>
                    ))}
                  </Form.Control>
                </Col>
              </Form.Group>
            </div>
          )}
          {selectedOption === 'select3' && (
            <div>
              <Form.Group as={Row}>
                <Form.Label htmlFor="list_themes_teacher" column sm={4}>Перечень тем руководителя</Form.Label>
                <Col sm={8}>
                  <Form.Control as="select" className={styles_base.form_control} id="list_themes_teacher" onChange={(e) => this.handleThemeSelect(parseInt(e.target.value))}>
                    <option value={0}>-</option>
                    {filteredTeacherThemes.map(theme => (
                      <option key={theme.id} value={theme.id}>{theme.name}</option>
                    ))}
                  </Form.Control>
                </Col>
              </Form.Group>
            </div>
          )}
        </div>
        {selectedTheme && (
          <div className='mb-3'>
            <h6>Выбранная тема:</h6>
            <Row className='justify-content-center'>
              <Col md={6}>Название организации</Col>
              <Col md={6}>{selectedTheme.company.name ? selectedTheme.company.name : 'отсутствует'}</Col>
            </Row>
            <Row className='justify-content-center'>
              <Col md={6}>ФИО ответственного</Col>
              <Col md={6}>{selectedTheme.company.last_name_of_responsible ? selectedTheme.company.last_name_of_responsible : 'отсутствует'}
                {selectedTheme.company.first_name_of_responsible ? selectedTheme.company.first_name_of_responsible : ' '}
                {selectedTheme.company.patronymic_of_responsible ? selectedTheme.company.patronymic_of_responsible : ' '}
              </Col>
            </Row>
            <Row className='justify-content-center'>
              <Col md={6}>Должность ответственного</Col>
              <Col md={6}>{selectedTheme.company.job_title_of_responsible ? selectedTheme.company.job_title_of_responsible : 'отсутствует'}</Col>
            </Row>
            <Row className='justify-content-center'>
              <Col md={6}>ВКР по заявке</Col>
              <Col md={6}>{selectedTheme.fqw_by_application ? selectedTheme.fqw_by_application : 'отсутствует'}</Col>
            </Row>
          </div>
        )}
        {selectedOption === 'select1' && (
          <div>
            <Form.Group as={Row} controlId="organization_link">
              <Form.Label column sm={4}>Связь темы ВКР с организацией</Form.Label>
              <Col sm={8} className="d-flex align-items-center">
                <Form.Check 
                  type="radio" 
                  name="have_organization" 
                  value="no" 
                  label="Не связана" 
                  className="mr-2" 
                  checked={!haveOrganization}
                  onChange={() => this.setState({ haveOrganization: false })}
                />
                <Form.Check 
                  type="radio" 
                  name="have_organization" 
                  value="yes" 
                  label="Связана" 
                  className="ml-2" 
                  checked={haveOrganization}
                  onChange={() => this.setState({ haveOrganization: true })}
                />
              </Col>
            </Form.Group>
            {haveOrganization && (
              <div>
                <Form.Group as={Row} controlId="organization_name_field">
                  <Form.Label column sm={4}>Наименование организации</Form.Label>
                  <Col sm={8}>
                    <Form.Control type="text" className={styles_base.form_control} placeholder="Введите наименование организации" />
                  </Col>
                </Form.Group>
                <Form.Group as={Row} controlId="fio_responsible_field">
                  <Form.Label column sm={4}>ФИО ответственного</Form.Label>
                  <Col sm={8}>
                    <Form.Control type="text" className={styles_base.form_control} placeholder="ФИО ответственного" />
                  </Col>
                </Form.Group>
                <Form.Group as={Row} controlId="post_responsible_field">
                  <Form.Label column sm={4}>Должность ответственного</Form.Label>
                  <Col sm={8}>
                    <Form.Control type="text" className={styles_base.form_control} placeholder="Должность ответственного" />
                  </Col>
                </Form.Group>
                <Form.Group as={Row} controlId="additional_resposible">
                  <Form.Label htmlFor="additional_info" column sm={4}>Дополнительно</Form.Label>
                  <Col sm={8}>
                    <Form.Control type="text" className={styles_base.form_control} placeholder="Дополнительная информация" />
                  </Col>
                </Form.Group>
              </div>
            )}
          </div>
        )}
      </div>
    );
  }
}
