import React, { Component } from 'react'
import BaseModal from '../BaseModal/BaseModal'
import { Modal, Button, Form, Row, Col } from 'react-bootstrap';
import styles_base from '../BaseModal/BaseModal.module.css'
import styles from '../ThemeListModal/ThemeListModal.module.css'
import {Link} from 'react-router-dom'

export default class ThemeListModal extends Component {
    render() {
        const { show, handleClose, darkMode, tableData, name, type_of_user } = this.props;
        const themesToShow = tableData.map(row => row.theme);
        const modalstyle = {
            modal: styles.container_modal 
          };

          let roleText;
          switch (type_of_user) {
            case 'руководитель':
              roleText = 'Руководитель: ';
              break;
            case 'секретарь ГЭК':
              roleText = 'Секретарь ГЭК: ';
              break;
            default:
              roleText = 'Неизвестный тип пользователя: ';
          }        
        const content = (
            <>

            <p class='fw-bold'> Руководитель: {name} </p>

            {themesToShow.map((theme, index) => (
                <p key={index} className='my-n3'>{theme}</p>
              ))}

              </>
          );
          const footer = (
            <Row className="justify-content-between px-3">
                <Col md={5}>
              <Button variant='none' className={styles_base.button_cancel} onClick={handleClose}>Отмена</Button>
              </Col>
              <Col md={5}>
              
              <Link to={{
                pathname: "/edit_theme_list_teacher",
                state: { tableData: tableData }
              }}>
              <Button variant='none' className={styles_base.button_next} onClick={handleClose}>Редактировать</Button>
              </Link>
              </Col>
              </Row>
          );

        return (
          <BaseModal show={this.props.show} 
          modalClass = {styles.container_modal}
          handleClose={this.props.handleClose} 
          title={'Список тем'}
          content={content} 
          darkMode={darkMode}
          sourceData={tableData}
          footer={footer}
          > 
            </BaseModal>
        )
      }
    }
