import { Button } from 'react-bootstrap';

const Sort = ({ column }) => {
    const { canSort, isSortedDesc, toggleSortBy } = column;
  
    const handleSortChange = () => {
      toggleSortBy(!isSortedDesc, true);
    };
  
    return (
      <div>
        {canSort && (
          <Button variant="none" onClick={handleSortChange}>
            {isSortedDesc ? 'Sort Desc' : 'Sort Asc'}
          </Button>
        )}
      </div>
    );
  };
export default Sort
