import React, { Component } from 'react'
import { Container, Row, Col, Button } from 'react-bootstrap';
import styles from './ChooseTeacher.module.css';

export default class ChooseTeacherCard extends Component {
  constructor(props) {
    super(props);
    // Определите список учителей с данными
    this.state = {
      teachers: [
        { name: 'Аникьева Марина Анатольевна', vacancies: 3 },
        { name: 'Брежнев Руслан Владимирович', vacancies: 3 },
        
      ]
    };
  }

  render() {
    return (
      <div className={styles.container}>
        <div className={styles.card}>
          <Row className="justify-content-center">
            <Col md={10}>
              <h4 className="mb-4 text-center">Выбрать руководителя</h4>
              <table className={styles.teachers_list} width="100%" cellPadding="7">
                <thead>
                  <tr>
                    <th>ФИО</th>
                    <th>Количество вакантных мест</th>
                    <th>Подать заявку</th>
                  </tr>
                </thead>
                <tbody>
                  {this.state.teachers.map((teacher, index) => (
                    <tr key={index}>
                      <td>{teacher.name}</td>
                      <td>{teacher.vacancies}</td>
                      <td><Button variant="link" className={styles.button_choose_teacher}>Подать заявку</Button></td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </Col>
          </Row>
        </div>
      </div>
    );
  }
}
