import React, { useState } from 'react';
import { Form, Button, Row, Col } from 'react-bootstrap';
import plus from '../../../../img/plus.png';
import del from '../../../../img/del.png';
import edit from '../../../../img/edit.png';
import styles from '../Teacher/EditThemeList.module.css';

const EditThemesListSecretaryForm = ({ themesList: initialThemesList,  teachers_list }) => {

  const [themesList, setThemesList] = useState(initialThemesList || []);
  const [editingIndex, setEditingIndex] = useState(null);
  const [editingData, setEditingData] = useState(null);
  const [editFormVisibleIndex, setEditFormVisibleIndex] = useState(null);
  const [isLinked, setIsLinked] = useState(false);
  const [filterAuthor, setFilterAuthor] = useState('');

  const [authorType, setAuthorType] = useState('');
  const [selectedTeacher, setSelectedTeacher] = useState(null);
  
  const addRow = () => {
    const newRow = {
      id: themesList.length ? Math.max(...themesList.map(item => item.id)) + 1 : 1,
      name: '',
      teacher: {
        user: data // Set the current user as the teacher
      },
      company: {
        name: '',
        lastNameOfResponsible: '',
        firstNameOfResponsible: '',
        patronymicOfResponsible: '',
        jobTitleOfResponsible: '',
        additionalInformation: '',
      },
      free: true,
      fqwByApplication: '',
      reviewer: {
        user: {
          id: null,
          lastName: null,
          firstName: null,
          patronymic: null
        }
      },
      byStudent: false,
      department: false,
    };

    const updatedThemesList = [newRow, ...themesList];
    setThemesList(updatedThemesList);
    setEditingIndex(newRow.id);
    setEditingData(newRow);
    setAuthorType('');
  };

  const handleSave = () => {
    const updatedThemesList = themesList.map(item =>
      item.id === editingIndex ? { ...item, ...editingData } : item
    );
    setThemesList(updatedThemesList);
    setEditFormVisibleIndex(null); // Set null to hide the edit form
    setEditingIndex(null);
    setEditingData(null);
  };

  const handleFilterAuthorChange = (e) => {
    setFilterAuthor(e.target.value);
  };

  const filteredThemesList = themesList.filter(item => {
    // If it's a new row (without an author), show it anyway
    if (!item.teacher.user.firstName && !item.teacher.user.lastName && !item.teacher.user.patronymic && !item.byStudent && !item.department) {
      return true;
    }

    if (item.teacher.user.firstName && item.teacher.user.lastName && item.teacher.user.patronymic) {
      return `${item.teacher.user.lastName} ${item.teacher.user.firstName} ${item.teacher.user.patronymic}`.toLowerCase().includes(filterAuthor.toLowerCase());
    } else if (item.byStudent) {
      return 'предложено студентом'.toLowerCase().includes(filterAuthor.toLowerCase());
    } else if (item.department) {
      return 'кафедра сии'.toLowerCase().includes(filterAuthor.toLowerCase());
    } else {
      return true;
    }
  });

  const handleEditClick = (index) => {
    const themeToEdit = themesList.find(item => item.id === index);
    setEditingIndex(index);
    setEditingData(themeToEdit);
    setEditFormVisibleIndex(index);
    setIsLinked(!!themeToEdit.company.name);
    setAuthorType(
      themeToEdit.byStudent ? 'student' :
      themeToEdit.department ? 'department' :
      'teacher'
    );
  };

  const handleCancel = () => {
    setEditFormVisibleIndex(null);
    if (editingIndex === null) {
      const newData = themesList.filter(item => item.id !== editingIndex);
      setThemesList(newData);
    }
    setEditingIndex(null);
  };

  const handleDeleteClick = (index) => {
    const newData = themesList.filter(item => item.id !== index);
    setThemesList(newData);
    if (editFormVisibleIndex === index) {
      setEditFormVisibleIndex(null);
    }
    setEditingIndex(null);
  };

  const handleInputChange = (e) => {
    const { name, value, type, checked } = e.target;
    const updatedEditingData = {
      ...editingData,
      [name]: type === 'checkbox' ? checked : value,
    };
    setEditingData(updatedEditingData);
  };

  const handleCompanyChange = (e) => {
    const { name, value } = e.target;
    const updatedEditingData = {
      ...editingData,
      company: {
        ...editingData.company,
        [name]: value,
      },
    };
    setEditingData(updatedEditingData);
  };

  const handleReviewerChange = (e) => {
    const { name, value } = e.target;
    const updatedReviewer = {
      ...editingData.reviewer.user,
      [name]: value,
    };
    const updatedEditingData = {
      ...editingData,
      reviewer: {
        user: updatedReviewer,
      },
    };
    setEditingData(updatedEditingData);
  };

  const handleRadioChange = (value) => {
    setIsLinked(value === 'yes');
    if (value === 'no') {
      // If "No" option is selected, clear the organization fields
      const updatedEditingData = {
        ...editingData,
        company: {
          name: '',
          lastNameOfResponsible: '',
          firstNameOfResponsible: '',
          patronymicOfResponsible: '',
          jobTitleOfResponsible: '',
          additionalInformation: '',
        },
      };
      setEditingData(updatedEditingData);
    }
  };

  const handleTeacherSelectChange = (e) => {
    const teacherId = e.target.value;
    const selectedTeacher = teachersList.find(teacher => teacher.user.id === parseInt(teacherId));
  
    // Set the selected teacher in the state
    setSelectedTeacher(selectedTeacher);
  
    const updatedEditingData = {
      ...editingData,
      teacher: {
        user: selectedTeacher.user
      }
    };
  
    setEditingData(updatedEditingData);
  };
  
  
  // In the author type change handler
  const handleAuthorTypeChange = (e) => {
    const { value } = e.target;
    setAuthorType(value);
    
    // Reset the selected teacher if the author type is changed
    setSelectedTeacher(null);
    
    // Set the appropriate flags depending on the selected author type
    switch (value) {
      case 'teacher':
        setEditingData(prevState => ({
          ...prevState,
          byStudent: false,
          department: false
        }));
        break;
      case 'department':
        setEditingData(prevState => ({
          ...prevState,
          byStudent: false,
          department: true
        }));
        break;
      case 'student':
        setEditingData(prevState => ({
          ...prevState,
          byStudent: true,
          department: false
        }));
        break;
      default:
        break;
    }
  };
  
  // Return value for displaying the full name in the table
  const getTeacherName = (item) => {
    if (item.teacher.user.firstName && item.teacher.user.lastName && item.teacher.user.patronymic) {
      return `${item.teacher.user.lastName} ${item.teacher.user.firstName} ${item.teacher.user.patronymic}`;
    } else if (item.byStudent) {
      return 'Предложено студентом';
    } else if (item.department) {
      return 'Кафедра СИИ';
    } else {
      return ''; // For new records without an author
    }
  };
  
  return (
    <>
      <h4 className="mb-4 text-center">Редактирование списка тем ВКР</h4>
      <Form.Group as={Row} controlId="filterAuthor" className="justify-content-center">
        <Form.Label column sm={2} className="text-center my-3">Фильтр по автору</Form.Label>
        <Col sm={6}>
          <Form.Control
            type="text"
            value={filterAuthor}
            onChange={handleFilterAuthorChange}
            placeholder="Введите имя автора для фильтрации"
            className={styles.block_form}
          />
        </Col>
      </Form.Group>

      <div className={styles.catch}>
        <table className={styles.table_themes}>
          <thead className="text-center">
            <tr>
              <th rowSpan="2" width="10%">Автор</th>
              <th rowSpan="2" width="50%">Наименование темы ВКР</th>
              <th colSpan="4" className="no-filter">Организация</th>
              <th rowSpan="2" width="5%">ВКР по заявке</th>
              <th rowSpan="2" width="5%">Ревьювер</th>
              <th rowSpan="2">Занята</th>
              <th rowSpan="2" className="no-filter">Действия</th>
              <th className="no-filter">
                <img id="addRowButton" src={plus} onClick={addRow} className={styles.img} alt="add" />
              </th>
            </tr>
            <tr>
              <th width="5%">Наименование</th>
              <th width="5%">Фамилия</th>
              <th width="5%">Имя</th>
              <th width="5%">Отчество</th>
            </tr>
          </thead>
          <tbody className="text-center">
            {filteredThemesList.map((item, index) => (
              <React.Fragment key={index}>
                <tr className={editFormVisibleIndex === item.id ? styles.hiddenRow : ''}>
                  <td>{getTeacherName(item)}</td>
                  <td>{item.name}</td>
                  <td>{item.company.name}</td>
                  <td>{item.company.lastNameOfResponsible}</td>
                  <td>{item.company.firstNameOfResponsible}</td>
                  <td>{item.company.patronymicOfResponsible}</td>
                  <td>{item.fqwByApplication}</td>
                  <td>{item.reviewer.user.lastName} {item.reviewer.user.firstName} {item.reviewer.user.patronymic}</td>
                  <td>
                    <input
                      type="checkbox"
                      checked={!item.free}
                      readOnly
                    />
                  </td>
                  <td>
                    <img id="editRowButton" src={edit} onClick={() => handleEditClick(item.id)} className={styles.img} alt="edit" />
                    <img id="deleteRowButton" src={del} onClick={() => handleDeleteClick(item.id)} className={styles.img} alt="delete" />
                  </td>
                </tr>
                {editFormVisibleIndex === item.id && (
                  <tr className={styles.editRow}>
                    <td colSpan="11">
                      <Form>
                        <Row className="mb-3">
                          <Col>
                            <Form.Group controlId="name">
                              <Form.Label>Наименование темы ВКР</Form.Label>
                              <Form.Control
                                type="text"
                                name="name"
                                value={editingData?.name || ''}
                                onChange={handleInputChange}
                              />
                            </Form.Group>
                          </Col>
                        </Row>
                        <Row className="mb-3">
                          <Col>
                            <Form.Group controlId="teacher">
                              <Form.Label>Автор темы</Form.Label>
                              <Form.Control as="select" value={authorType} onChange={handleAuthorTypeChange}>
                                <option value="">Выберите тип автора</option>
                                <option value="teacher">Преподаватель</option>
                                <option value="student">Предложено студентом</option>
                                <option value="department">Кафедра СИИ</option>
                              </Form.Control>
                            </Form.Group>
                          </Col>
                        </Row>
                        {authorType === 'teacher' && (
                          <Row className="mb-3">
                            <Col>
                              <Form.Group controlId="teacherSelect">
                                <Form.Label>Выберите преподавателя</Form.Label>
                                <Form.Control as="select" onChange={handleTeacherSelectChange}>
                                  <option value="">Выберите преподавателя</option>
                                  {teachersList.map((teacher) => (
                                    <option key={teacher.user.id} value={teacher.user.id}>
                                      {teacher.user.lastName} {teacher.user.firstName} {teacher.user.patronymic}
                                    </option>
                                  ))}
                                </Form.Control>
                              </Form.Group>
                            </Col>
                          </Row>
                        )}
                        <Row className="mb-3">
                          <Col>
                            <Form.Group controlId="linkedOrganization">
                              <Form.Label>ВКР связана с организацией</Form.Label>
                              <div className="mb-3">
                                <Form.Check
                                  inline
                                  type="radio"
                                  label="Да"
                                  name="linkedOrganization"
                                  value="yes"
                                  checked={isLinked}
                                  onChange={() => handleRadioChange('yes')}
                                />
                                <Form.Check
                                  inline
                                  type="radio"
                                  label="Нет"
                                  name="linkedOrganization"
                                  value="no"
                                  checked={!isLinked}
                                  onChange={() => handleRadioChange('no')}
                                />
                              </div>
                            </Form.Group>
                          </Col>
                        </Row>
                        {isLinked && (
                          <>
                            <Row className="mb-3">
                              <Col>
                                <Form.Group controlId="company.name">
                                  <Form.Label>Наименование организации</Form.Label>
                                  <Form.Control
                                    type="text"
                                    name="name"
                                    value={editingData?.company?.name || ''}
                                    onChange={handleCompanyChange}
                                  />
                                </Form.Group>
                              </Col>
                            </Row>
                            <Row className="mb-3">
                              <Col>
                                <Form.Group controlId="company.lastNameOfResponsible">
                                  <Form.Label>Фамилия ответственного</Form.Label>
                                  <Form.Control
                                    type="text"
                                    name="lastNameOfResponsible"
                                    value={editingData?.company?.lastNameOfResponsible || ''}
                                    onChange={handleCompanyChange}
                                  />
                                </Form.Group>
                              </Col>
                              <Col>
                                <Form.Group controlId="company.firstNameOfResponsible">
                                  <Form.Label>Имя ответственного</Form.Label>
                                  <Form.Control
                                    type="text"
                                    name="firstNameOfResponsible"
                                    value={editingData?.company?.firstNameOfResponsible || ''}
                                    onChange={handleCompanyChange}
                                  />
                                </Form.Group>
                              </Col>
                              <Col>
                                <Form.Group controlId="company.patronymicOfResponsible">
                                  <Form.Label>Отчество ответственного</Form.Label>
                                  <Form.Control
                                    type="text"
                                    name="patronymicOfResponsible"
                                    value={editingData?.company?.patronymicOfResponsible || ''}
                                    onChange={handleCompanyChange}
                                  />
                                </Form.Group>
                              </Col>
                            </Row>
                            <Row className="mb-3">
                              <Col>
                                <Form.Group controlId="company.jobTitleOfResponsible">
                                  <Form.Label>Должность ответственного</Form.Label>
                                  <Form.Control
                                    type="text"
                                    name="jobTitleOfResponsible"
                                    value={editingData?.company?.jobTitleOfResponsible || ''}
                                    onChange={handleCompanyChange}
                                  />
                                </Form.Group>
                              </Col>
                            </Row>
                            <Row className="mb-3">
                              <Col>
                                <Form.Group controlId="company.additionalInformation">
                                  <Form.Label>Дополнительная информация</Form.Label>
                                  <Form.Control
                                    type="text"
                                    name="additionalInformation"
                                    value={editingData?.company?.additionalInformation || ''}
                                    onChange={handleCompanyChange}
                                  />
                                </Form.Group>
                              </Col>
                            </Row>
                          </>
                        )}
                        <Row className="mb-3">
                          <Col>
                            <Form.Group controlId="fqwByApplication">
                              <Form.Label>ВКР по заявке</Form.Label>
                              <Form.Control
                                type="text"
                                name="fqwByApplication"
                                value={editingData?.fqwByApplication || ''}
                                onChange={handleInputChange}
                              />
                            </Form.Group>
                          </Col>
                        </Row>
                        <Row className="mb-3">
                          <Col>
                            <Form.Group controlId="reviewer.lastName">
                              <Form.Label>Фамилия ревьювера</Form.Label>
                              <Form.Control
                                type="text"
                                name="lastName"
                                value={editingData?.reviewer?.user?.lastName || ''}
                                onChange={handleReviewerChange}
                              />
                            </Form.Group>
                          </Col>
                          <Col>
                            <Form.Group controlId="reviewer.firstName">
                              <Form.Label>Имя ревьювера</Form.Label>
                              <Form.Control
                                type="text"
                                name="firstName"
                                value={editingData?.reviewer?.user?.firstName || ''}
                                onChange={handleReviewerChange}
                              />
                            </Form.Group>
                          </Col>
                          <Col>
                            <Form.Group controlId="reviewer.patronymic">
                              <Form.Label>Отчество ревьювера</Form.Label>
                              <Form.Control
                                type="text"
                                name="patronymic"
                                value={editingData?.reviewer?.user?.patronymic || ''}
                                onChange={handleReviewerChange}
                              />
                            </Form.Group>
                          </Col>
                        </Row>
                        <Row className="mb-3">
                          <Col>
                            <Form.Group controlId="free">
                              <Form.Label>Занята</Form.Label>
                              <div>
                                <Form.Check
                                  type="checkbox"
                                  name="free"
                                  checked={!editingData?.free}
                                  onChange={handleInputChange}
                                />
                              </div>
                            </Form.Group>
                          </Col>
                        </Row>
                        <div className="text-center">
                          <Button variant="secondary" onClick={handleCancel} className="me-2">Отмена</Button>
                          <Button variant="primary" onClick={handleSave}>Сохранить</Button>
                        </div>
                      </Form>
                    </td>
                  </tr>
                )}
              </React.Fragment>
            ))}
          </tbody>
        </table>
      </div>
    </>
  );
};

export default EditThemesListSecretaryForm;
