import React, { Component } from 'react'
import {Image} from 'react-bootstrap'
import styles from '../../ProfileBlocks/ProfileBlocks.module.css';
import more from '../../../img/more.png'
import ThemeListModal from '../../Modals/ThemeListModal/ThemeListModal';
export default class ThemesCard extends Component {
    render() {
      const { tableData } = this.props;
      const themesToShow = tableData.slice(0, 3).map(row => row.theme);
      const [showModal, setShowModal] = useState(null);
      const handleShowModal = (modalName) => {
        setShowModal(modalName);
      };
      const handleCloseModal = () => {
        setShowModal(null);
      };

      return (
        <div className={styles.card}>
          <p className="text-center px-1 py-2 fw-bold">Список тем</p>
          <div>
            {themesToShow.map((theme, index) => (
              <p key={index} class='my-n3'>{theme}</p>
            ))}
          </div>
          <Image src={more} className={styles.more}  onClick={() => handleShowModal('themesTist')} alt="profile_pic" loading="lazy"/>
          <ThemeListModal/>
        </div>
       
      );
    }
  }
