import React, { useState } from 'react';
import { useAsyncDebounce } from 'react-table';
import { createPortal } from 'react-dom';
import { Button, Form } from 'react-bootstrap';

const Filter = ({
  column: { filterValue, setFilter, preFilteredRows, id }
}) => {
  const [isOpen, setIsOpen] = useState(false);

  const toggleModal = () => {
    setIsOpen(!isOpen);
  };

  const applyFilter = (value) => {
    setFilter(value || undefined);
    toggleModal();
  };

  const options = React.useMemo(() => {
    const options = new Set();
    preFilteredRows.forEach(row => {
      options.add(row.values[id]);
    });
    return [...options.values()];
  }, [id, preFilteredRows]);

  const onChange = useAsyncDebounce(value => {
    applyFilter(value);
  }, 200);

  return (
    <>
      <Button onClick={toggleModal}>Filter</Button>
      {isOpen && (
        createPortal(
          <div style={{ position: 'absolute', top: 'calc(100% + 8px)', left: 0, zIndex: 999, background: 'white', boxShadow: '0px 8px 16px 0px rgba(0,0,0,0.2)', padding: '16px' }}>
            <Form.Control as="select" value={filterValue} onChange={e => onChange(e.target.value)}>
              <option value="">All</option>
              {options.map((option, index) => (
                <option key={index} value={option}>
                  {option}
                </option>
              ))}
            </Form.Control>
            <Button variant="secondary" onClick={toggleModal} style={{ marginTop: '8px' }}>Close</Button>
            <Button variant="primary" onClick={() => applyFilter('')} style={{ marginLeft: '8px', marginTop: '8px' }}>Clear</Button>
          </div>,
          document.body
        )
      )}
    </>
  );
};

export default Filter;
