import React from 'react'
import styles from './SupervisedStudents.module.css'
import { Row, Col } from 'react-bootstrap'

function SupervisedStudentsCard({ teacherData, requestData, work_schedule, work_protection }) {
  return (
    <>
      <h4 className="mb-4 text-center">Курируемые выпускники</h4>
      <div className={styles.block}>
        {requestData.map((request) => {
          if (request.is_accepted_request === true) {
            const protection = work_protection.find(wp => wp.id_request === request.id);
            const schedules = work_schedule.filter(sch => sch.id_request === request.id);
            const today = new Date();

            const isWithinDateRange = (start, end) => {
              const startDate = new Date(start);
              const endDate = new Date(end);
              return today >= startDate && today <= endDate;
            };

            const activeSchedule = schedules.find(schedule => 
              schedule.start && schedule.end && isWithinDateRange(schedule.start, schedule.end)
            );

            return (
              <div key={request.id}>
                <Row className='justify-content-center'>
                  <Col md={3}>{request.student.surname} {request.student.name} {request.student.patronymic}</Col>
                  <Col md={4}>{request.student.group}</Col>
                  <Col md={4}>
                  <Button>
                    Действия
                  </Button>
                  </Col>
                </Row>
                <Row className='justify-content-center'>
                  <Col md={4}>Тема:</Col>
                  <Col md={7}>{request.theme_str}</Col>
                </Row>

                <Row className='justify-content-center'>
                  <Col md={4}>Этап работы:</Col>
                  {activeSchedule ? (
                    <Col md={7}>{activeSchedule.stage_name}</Col>
                  ) : (
                    <Col md={7}>график не был создан</Col>
                  )}
                </Row>

                <Row className='justify-content-center'>
                  <Col md={4}>Дата и время защиты:</Col>
                  {protection ? (
                    <Col md={7}>{protection.date}</Col>
                  ) : (
                    <Col md={7}>график защит ВКР не создан секретарем ГЭК</Col>
                  )}
                </Row>

                <Row className='justify-content-center'>
                  <Col md={4}>Тип ВКР:</Col>
                  <Col md={7}>{request.type_of_fqw}</Col>
                </Row>

                {request.additional_information && (
                  <Row className="justify-content-center">
                    <Col md={4}>Дополнительная информация:</Col>
                    <Col md={7}>{request.additional_information}</Col>
                  </Row>
                )}
              </div>
            );
          } else {
            return null; 
          }
        })}
      </div>
    </>
  )
}

export default SupervisedStudentsCard;
