import React, { useState } from 'react';
import BasePage from './BasePage'
import styles from '../base.css';
import Header from '../components/Header/Header.js';
import Background from '../components/Background/Background.js';
import Footer from '../components/Footer/Footer.js';
import SideMenu from '../components/SideMenu/SideMenu.js';

const BasePageWithCard = ({ isAuthenticated, toggleMenu, isSideMenuOpen, children }) => {
  const [darkMode, setDarkMode] = useState(false);

  const toggleTheme = () => {
    setDarkMode(prevDarkMode => !prevDarkMode);
  };
    return (
        <Background darkMode={darkMode} toggleTheme={toggleTheme}>
        <Header isAuthenticated={isAuthenticated} toggleMenu={toggleMenu} toggleTheme={toggleTheme} isSideMenuOpen={isSideMenuOpen} darkMode={darkMode}  />
        <SideMenu toggleMenu={toggleMenu} isSideMenuOpen={isSideMenuOpen} />
        <div className={styles.card}>
       {children}
       </div>
        <Footer darkMode={darkMode} />
      </Background>

    );
  };
  export default BasePageWithCard;
