import React, { useState, useEffect } from 'react';
import BasePageWithCard from '../BasePages/BasePageWithCard';
import CreateScheduleProtectionForm from '../../components/Forms/CreateScheduleProtectionForm/CreateScheduleProtectionForm';
import axios from 'axios';

function CreateSheduleProtection({ students_list_for_schedule }) {
  const [groupsForSchedule, setGroupsForSchedule] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    async function fetchGroups() {
      try {
        const response = await axios.get('https://api.example.com/groups/');
        setGroupsForSchedule(response.data.groups);
        setLoading(false);
      } catch (error) {
        setError('Ошибка при загрузке данных о группах.');
        setLoading(false);
      }
    }

    fetchGroups();
  }, []);

  return (
    <BasePageWithCard isAuthenticated={true}>
      {loading ? (
        <p>Загрузка данных...</p>
      ) : error ? (
        <p>{error}</p>
      ) : (
        <CreateScheduleProtectionForm
          groups_for_schedule={groupsForSchedule}
          students_list_for_schedule={students_list_for_schedule}
        />
      )}
    </BasePageWithCard>
  );
}

export default CreateSheduleProtection;
