import React from 'react';
import { Modal, Table } from 'react-bootstrap';
import BaseModal from '../BaseModal/BaseModal';

const ScheduleFQWModal = ({ show, handleClose, darkMode, schedule_stud_prof }) => {
console.log(schedule_stud_prof)
  const stages = Array.isArray(schedule_stud_prof) ? schedule_stud_prof : [];

  return (
    <BaseModal 
      show={show} 
      handleClose={handleClose} 
      title={'График работы над ВКР'}
      darkMode={darkMode}
    >
      <Table striped bordered hover>
        <thead>
          <tr>
            <th>Этап</th>
            <th>Начало</th>
            <th>Конец</th>
            <th>Результат</th>
            <th>Отметка о выполнении</th>
            <th>Пропуск</th>
          </tr>
        </thead>
        <tbody>
          {stages.map(stage => (
            <tr key={stage.id}>
              <td>{stage.stage_name}</td>
              <td>{stage.start}</td>
              <td>{stage.end}</td>
              <td>{stage.result}</td>
              <td>{stage.completion_mark}</td>
              <td>{stage.absence}</td>
            </tr>
          ))}
        </tbody>
      </Table>
    </BaseModal>
  );
};

export default ScheduleFQWModal;
