import React, { useState } from 'react';
import styles from '../ProfileBlocks.module.css';
import application from './application.png';
import graph from './graph.png';
import form from './form.png';
import { Row, Col, Modal, Button } from 'react-bootstrap';
import { Link } from 'react-router-dom';
import CustomTooltip from '../../../Tooltip/Tooltip';
import info from '../../../../img/info.png';

function ActionsCard() {
  const [showModal, setShowModal] = useState(false);
  const [action, setAction] = useState('');

  const handleModalClose = () => setShowModal(false);

  const handleActionSelection = (selectedAction) => {
    setAction(selectedAction);
    setShowModal(false);
  };

  return (
    <div className={`${styles.card} actions`}>
      <p className="text-center px-1 py-2 fw-bold">Действия</p>

      <Row className='flex justify-content-center'>
        <Col md={10}>
          <CustomTooltip text="Автоматическая генерация заявлений">
            <h6 className="fw-bold">Генерация документов <img className='mx-2' src={info} width={15} height={15} alt="info" /></h6>
          </CustomTooltip>
        </Col>
        <Col md={1}></Col>
      </Row>

      <Row className='flex justify-content-center mb-1'>
        <Col md={10}>
          <Link to='/generate_application'>Заявление на утверждение темы или руководителя ВКР</Link>
        </Col>
        <Col md={1}>
          <Link to='/generate_application'><img src={application} width="20" height="20" alt="profile_pic" loading="lazy" /></Link>
        </Col>
      </Row>

      <Modal show={showModal} onHide={handleModalClose}>
        <Modal.Header closeButton>
          <Modal.Title>Выберите действие</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Button variant="primary" onClick={() => handleActionSelection('generate_topic')}>
            Генерировать заявление на утверждение темы ВКР
          </Button>
          <Button variant="primary" onClick={() => handleActionSelection('generate_supervisor')}>
            Генерировать заявление на утверждение руководителя ВКР
          </Button>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleModalClose}>
            Закрыть
          </Button>
        </Modal.Footer>
      </Modal>
    </div>
  );
}

export default ActionsCard;
