import React, { useState } from 'react';
import styles from "./GenerateApplicationForm.module.css";
import styles_base from "../../Forms/FormsBase.module.css";
import BlockInfo from '../../BlockInfo/BlockInfo';
import { Form, Button, Col, Row } from 'react-bootstrap';
import { saveAs } from 'file-saver';
import PizZip from 'pizzip';
import Docxtemplater from 'docxtemplater';
import word from './ApplicationForGraduateWork.docx'

const направления = {
  "09.03.02-Информационные системы и технологии": "09.03.02.30-Информационные системы и технологии",
  "09.04.01-Информатика и вычислительная техника": "09.04.01.10-Интеллектуальные информационные системы",
  "09.04.02-Информационные системы и технологии": [
    "09.04.02.01-Информационно-управляющие системы",
    "09.04.02.05-Информационные системы дистанционного зондирования Земли"
  ]
};

function GenerateApplicationForm({studentData, teacherData, requestData}) {
    const [selectedKey, setSelectedKey] = useState('');
    const [selectedValue, setSelectedValue] = useState('');
    const [showFields, setShowFields] = useState(false);
  
    const handleKeyChange = (event) => {
        const key = event.target.value;
        setSelectedKey(key);
        setSelectedValue(направления[key] || '');
      };
    
      const handleValueChange = (event) => {
        const value = event.target.value;
        setSelectedValue(value);
        const key = Object.keys(направления).find((k) => {
          const val = направления[k];
          if (Array.isArray(val)) {
            return val.includes(value);
          }
          return val === value;
        });
        setSelectedKey(key || '');
      };
      
  
    const handleRadioChange = (e) => {
      setShowFields(e.target.value === 'option1');
    };
    const fullname = studentData.surname + ' ' + studentData.name + ' ' + studentData.patronymic
    const fullnameTeacher = teacherData.surname + ' ' + teacherData.name + ' ' + teacherData.patronymic
     
    const generateDocument = () => {
      // Загрузка шаблона документа
      const templateFile = word;
      const xhr = new XMLHttpRequest();
      xhr.open('GET', templateFile, { responseType: 'arraybuffer' });
      xhr.onload = () => {
          const data = new Uint8Array(xhr.response);
          const zip = new PizZip(data);
          let doc;
          try {
              doc = new Docxtemplater(zip);
          } catch (error) {
              console.error('Ошибка создания документа:', error);
              return;
          }
          // Замена заполнителей данными
          doc.setData({
            "{{ studentData.phone_number}}": studentData.phone_number,
            "{{ studentData.email}}": studentData.email,
            "{{fullname}}": fullname,
            "{{studentData.kurs}}": studentData.kurs,
            "{{studentData.group}}": studentData.group,
            "{{speciality}}": studentData.speciality,
            "{{profile}}": studentData.profile,
            "{{studentData.form_of_studying}}": studentData.form_of_studying,
            "{{ studentData.base_of_studying}}": studentData.base_of_studying,
            "{{requestData.theme_str}}": requestData.theme_str,
            "{{fullnameTeacher}}": fullnameTeacher,
            "{{teacherData.academic_degree}}": teacherData.academic_degree,
            "{{teacherData.academic_title}}": teacherData.academic_title,
            "{{teacherData.job_title}}": teacherData.job_title,
            "{{teacherData.place_of_work}}": teacherData.place_of_work
        });
        
          try {
              doc.render();
          } catch (error) {
              console.error('Ошибка рендеринга документа:', error);
              return;
          }
          const output = doc.getZip().generate({ type: 'blob' });
          // Сохранение документа на устройстве
          saveAs(output, 'generated_document.docx');
      };
      xhr.onerror = (error) => {
          console.error('Ошибка загрузки шаблона документа:', error);
      };
      xhr.send();
  };




  return (
<>
        <h4 className="mb-2 text-center">Данные для заявления на ВКР</h4>
        <Row className='justify-content-center'>
            <Col md={10}>
        <BlockInfo>
          Проверьте данные в форме, при необходимости внесите правки и сформируйте заявление на утверждение темы ВКР
        </BlockInfo>
        <Form className="px-4 py-2 m-2">
          <h5 className="text-center mb-2">Данные выпускника</h5>
          <div className={styles_base.block_form}>

            <Form.Group as={Row}>
              <Form.Label column sm={4}>ФИО</Form.Label>
              <Col sm={8}>
                <Form.Control type="text" className={styles_base.form_control} placeholder="ФИО" value={fullname} />
              </Col>
            </Form.Group>
            <Form.Group as={Row}>

        <Form.Label column sm={4}>Контактный телефон</Form.Label>
        <Col sm={8}>
            <Form.Control type="text" className={styles_base.form_control} placeholder="номер телефона" value={studentData.phone_number} />
        </Col>
        </Form.Group>

        <Form.Group as={Row}>
      <Form.Label column sm={4}>Email</Form.Label>
      <Col sm={8}>
        <Form.Control type="text"  className={styles_base.form_control} placeholder="email" value={studentData.email}/>
      </Col>
    </Form.Group>
    <Form.Group as={Row}>
      <Form.Label column sm={4}>Курс</Form.Label>
      <Col sm={8}>
        <Form.Control type="text" className={styles_base.form_control} placeholder="4" value={studentData.kurs}/>
      </Col>
    </Form.Group>
    <Form.Group as={Row}>
      <Form.Label column sm={4}>Группа</Form.Label>
      <Col sm={8}>
        <Form.Control type="text" className={styles_base.form_control} placeholder="группа" value={studentData.group}/>
      </Col>
    </Form.Group>
    <Form.Group as={Row}>
            <Form.Label column sm={4}>Направление подготовки/специальности:</Form.Label>
            <Col sm={8}>
            <Form.Control as="select" className={styles_base.form_control} value={selectedKey} onChange={handleKeyChange}>
            <option value="">Направление подготовки/специальности</option>
            {Object.keys(направления).map((key) => (
                <option key={key} value={key}>{key}</option>
            ))}
            </Form.Control>
            </Col>
          </Form.Group>
          <Form.Group as={Row}>
            <Form.Label column sm={4}>Профиль/специализация:</Form.Label>
            <Col sm={8}>
            <Form.Control as="select" className={styles_base.form_control} value={selectedValue} onChange={handleValueChange}>
                <option value="">Профиль/специализация</option>
                {Object.entries(направления).map(([key, value]) => (
                    Array.isArray(value) ? value.map((val) => (
                    <option key={val} value={val}>{val}</option>
                    )) : <option key={value} value={value}>{value}</option>
                ))}
                </Form.Control>


            </Col>
          </Form.Group>
          <Form.Group as={Row}>
            <Form.Label column sm={4}>Форма обучения</Form.Label>
            <Col sm={8}>
                <Form.Control as="select" className={styles_base.form_control} value={studentData.form_of_studying}>
                <option value="Очная">Очная</option>
                <option value="Заочная">Заочная</option>
                <option value="Очно-заочная">Очно-заочная</option>
                </Form.Control>
            </Col>
            </Form.Group>

            <Form.Group as={Row}>
            <Form.Label column sm={4}>Основа обучения</Form.Label>
            <Col sm={8}>
                <Form.Control as="select" className={styles_base.form_control} value={studentData.basis_of_study}>
                <option value="за счет бюджетных ассигнований федерального бюджета">за счет бюджетных ассигнований федерального бюджета</option>
                <option value="на условиях договора об оказании платных образовательных услуг">на условиях договора об оказании платных образовательных услуг</option>
                </Form.Control>
            </Col>
            </Form.Group>

            <Form.Group as={Row}>
            <Form.Label column sm={4}>Тема ВКР</Form.Label>
            <Col sm={8}>
                <Form.Control type="text" className={styles_base.form_control} placeholder="тема вкр" value={requestData.theme_str}/>
            </Col>
            </Form.Group>
          </div>
          <h5 className="my-4 text-center">Данные руководителя</h5>
          <div className={styles_base.block_form}>
          <Form.Group as={Row}>
            <Form.Label column sm={4}>ФИО руководителя</Form.Label>
            <Col sm={8}>
                <Form.Control type="text" className={styles_base.form_control} placeholder="ФИО руководителя" value={fullnameTeacher}/>
            </Col>
            </Form.Group>

            <Form.Group as={Row}>
            <Form.Label column sm={4}>Ученая степень</Form.Label>
            <Col sm={8}>
                <Form.Control type="text" className={styles_base.form_control} placeholder="ученая степень" value={teacherData.academic_degree}/>
            </Col>
            </Form.Group>

            <Form.Group as={Row}>
            <Form.Label column sm={4}>Звание</Form.Label>
            <Col sm={8}>
                <Form.Control type="text" className={styles_base.form_control} placeholder="звание" value={teacherData.academic_title}/>
            </Col>
            </Form.Group>

            <Form.Group as={Row}>
            <Form.Label column sm={4}>Должность</Form.Label>
            <Col sm={8}>
                <Form.Control type="text" className={styles_base.form_control} placeholder="должность" value={teacherData.job_title}/>
            </Col>
            </Form.Group>

            <Form.Group as={Row}>
            <Form.Label column sm={4}>Место работы</Form.Label>
            <Col sm={8}>
                <Form.Control type="text" className={styles_base.form_control} placeholder="место работы" value={teacherData.place_of_work}/>
            </Col>
            </Form.Group>

          </div>

          <h5 className="my-4 text-center">Данные консультанта</h5>
          <div className={styles_base.block_form}>
            <Form.Group as={Row}>
              <Form.Label column sm={5}>Есть консультант</Form.Label>
              <Col sm={7}>
                <Form.Check
                  inline
                  type="radio"
                  label="есть"
                  value="option1"
                  name="options"
                  onChange={handleRadioChange}
                />
                <Form.Check
                  inline
                  type="radio"
                  label="нет"
                  value="option2"
                  name="options"
                  onChange={handleRadioChange}
                />
              </Col>
            </Form.Group>
            {showFields && (
              <div id="fieldsToShow">
               <Form.Group as={Row}>
            <Form.Label column sm={4}>ФИО консультанта</Form.Label>
            <Col sm={8}>
              <Form.Control type="text" className={styles_base.form_control} placeholder="ФИО консультанта" />
            </Col>
          </Form.Group>
          <Form.Group as={Row}>
            <Form.Label column sm={4}>Ученая степень</Form.Label>
            <Col sm={8}>
              <Form.Control type="text" className={styles_base.form_control} placeholder="ученая степень" />
            </Col>
          </Form.Group>
          <Form.Group as={Row}>
            <Form.Label column sm={4}>Звание</Form.Label>
            <Col sm={8}>
              <Form.Control type="text" className={styles_base.form_control} placeholder="звание" />
            </Col>
          </Form.Group>
          <Form.Group as={Row}>
            <Form.Label column sm={4}>Должность</Form.Label>
            <Col sm={8}>
              <Form.Control type="text"className={styles_base.form_control}  placeholder="должность" />
            </Col>
          </Form.Group>
          <Form.Group as={Row}>
            <Form.Label column sm={4}>Место работы</Form.Label>
            <Col sm={8}>
              <Form.Control type="text" className={styles_base.form_control} placeholder="место работы" />
            </Col>
          </Form.Group>
          </div>
            )}
          </div>
          <Button className={styles.button_form} onClick={generateDocument}>Отправить</Button>
        </Form>
        </Col>
        </Row>
      </>
  );
}

export default GenerateApplicationForm;
