import React, { useState } from 'react';
import { useTable, useFilters } from 'react-table';
import * as XLSX from 'xlsx';
import { Button } from 'react-bootstrap';
import styles from '../../../container.module.css';
import Filter from '../Filter/Filter';

function SecretaryHugeTable({ info_for_table }) {
  const [tableData, setTableData] = useState(info_for_table.requests);
  const [editableCell, setEditableCell] = useState(null);
  const [hiddenColumns, setHiddenColumns] = useState([]);

  const columns = React.useMemo(
    () => [
      {
        Header: '№ п/п',
        accessor: (row, index) => index + 1,
        headerProps: { rowSpan: 2 },
        Filter: Filter,
        id: 'number'
      },
      // (оставьте остальные столбцы без изменений, просто добавьте уникальный id для каждого)
      {
        Header: 'Фамилия, Имя, Отчество',
        accessor: (row) => `${row.student.surname} ${row.student.name} ${row.student.patronymic}`,
        Filter: Filter,
        id: 'studentFullName'
      },
      {
        Header: 'Тема диплома',
        accessor: 'theme.name',
        Filter: Filter,
        id: 'themeName'
      },
      {
        Header: 'Руководитель диплома',
        accessor: (row) => `${row.teacher.surname} ${row.teacher.name} ${row.teacher.patronymic}`,
        Filter: Filter,
        id: 'teacherFullName'
      },
      {
        Header: 'Консультант',
        accessor: (row) => `${row.consultant.surname} ${row.consultant.name} ${row.consultant.patronymic}`,
        Filter: Filter,
        id: 'consultantFullName'
      },
      {
        Header: 'Рецензент',
        accessor: (row) => `${row.consultant.surname} ${row.consultant.name} ${row.consultant.patronymic}`,
        Filter: Filter,
        id: 'reviewerFullName'
      },
      {
        Header: 'организация',
        accessor: (row) => `${row.theme.organization.name} ${row.theme.organization.last_name_of_responsible} ${row.theme.organization.first_name_of_responsible} ${row.theme.organization.patronymic_name_of_responsible}
         ${row.theme.organization.job_title_of_resposible} ${row.theme.organization.additional_information}`,
        Filter: Filter,
        id: 'organization'
      },
      {
        Header: 'Телефон',
        accessor: (row) => `${row.student.phone}`,
        Filter: Filter,
        id: 'phone'
      },
      {
        Header: 'Email',
        accessor: (row) => `${row.student.email}`,
        Filter: Filter,
        id: 'email'
      },
      {
        Header: 'бюджет/платный',
        accessor: (row) => `${row.student.basis_of_study}`,
        Filter: Filter,
        id: 'basisOfStudy'
      },
      {
        Header: 'оценка на предзащите/ преддипломная практика',
        accessor: (row) => `${row.preprotection_grade}`,
        Filter: Filter,
        id: 'preprotectionGrade'
      },
      {
        Header: 'оценка на защите',
        accessor: (row) => `${row.workProtecton.final_grade}`,
        Filter: Filter,
        id: 'finalGrade'
      },
      {
        Header: 'дата рождения',
        accessor: (row) => `${row.student.date_of_birth}`,
        Filter: Filter,
        id: 'dateOfBirth'
      },
      {
        Header: 'год окончания предыдущ образования',
        accessor: (row) => `${row.student.year_of_prev_education_completion}`,
        Filter: Filter,
        id: 'yearOfPrevEducationCompletion'
      },
      {
        Header: 'форма предыдущего образования',
        accessor: (row) => `${row.student.form_of_prev_education}`,
        Filter: Filter,
        id: 'formOfPrevEducation'
      },
      {
        Header: 'год поступления',
        accessor: (row) => `${row.student.year_of_admission}`,
        Filter: Filter,
        id: 'yearOfAdmission'
      },
      {
        Header: 'особые отметки',
        accessor: (row) => `${row.student.special_marks}`,
        Filter: Filter,
        id: 'specialMarks'
      },
      {
        Header: 'вид ВКР',
        accessor: (row) => `${row.type_of_fqw}`,
        Filter: Filter,
        id: 'typeOfFqw'
      },
      {
        Header: 'ВКР по заявке',
        accessor: (row) => `${row.theme.fqw_by_application}`,
        Filter: Filter,
        id: 'fqwByApplication'
      },
      {
        Header: 'ВКР на англ. языке',
        accessor: (row) => (row.fqw_in_english ? 'да' : 'нет'),
        Filter: Filter,
        id: 'fqwInEnglish'
      },
      {
        Header: 'ВКР рекомендовано',
        accessor: (row) => `${row.fqw_recommended}`,
        Filter: Filter,
        id: 'fqwRecommended'
      },
      {
        Header: 'наличие спец. условий',
        accessor: (row) => (row.special_conditions ? 'да' : 'нет'),
        Filter: Filter,
        id: 'specialConditions'
      },
      {
        Header: 'приказы',
        columns: [
          {
            Header: 'допуск',
            accessor: (row) => `${row.workProtecton.final_grade}`,
            Filter: Filter,
            id: 'permission'
          },
          {
            Header: 'тема',
            accessor: (row) => `${row.workProtecton.final_grade}`,
            Filter: Filter,
            id: 'theme'
          },
          {
            Header: 'изменение темы',
            accessor: (row) => `${row.workProtecton.final_grade}`,
            Filter: Filter,
            id: 'themeChange'
          },
          {
            Header: 'рецензент',
            accessor: (row) => `${row.workProtecton.final_grade}`,
            Filter: Filter,
            id: 'reviewer'
          },
          {
            Header: 'отчисление',
            accessor: (row) => `${row.workProtecton.final_grade}`,
            Filter: Filter,
            id: 'expulsion'
          },
          {
            Header: 'с какого числа отчисление',
            accessor: (row) => `${row.workProtecton.final_grade}`,
            Filter: Filter,
            id: 'expulsionDate'
          }
        ]
      },
      {
        Header: 'отметки о выполнении',
        columns: [
          {
            Header: 'долги',
            accessor: (row) => `${row.student.number_of_debts}`,
            Filter: Filter,
            id: 'debts'
          },
          {
            Header: 'справка о выполнении уч. плана',
            accessor: (row) => `${row.student.certificate_of_curriculum_completion}`,
            Filter: Filter,
            id: 'curriculumCompletion'
          },
          {
            Header: 'сдана зачетка',
            accessor: (row) => `${row.student.record_book_submitted}`,
            Filter: Filter,
            id: 'recordBookSubmitted'
          },
          {
            Header: 'сдана пояснительная записка',
            accessor: (row) => (row.explanatory_note_submitted ? 'да' : 'нет'),
            Filter: Filter,
            id: 'explanatoryNoteSubmitted'
          },
          {
            Header: 'красный диплом',
            accessor: (row) => (row.student.diploma_with_honors ? 'да' : 'нет'),
            Filter: Filter,
            id: 'diplomaWithHonors'
          },
          {
            Header: 'согласие на публикацию',
            accessor: (row) => (row.publication_agree ? 'да' : 'нет'),
            Filter: Filter,
            id: 'publicationAgree'
          },
          {
            Header: 'отзыв',
            accessor: (row) => `${row.teacher_review}`,
            Filter: Filter,
            id: 'teacherReview'
          },
          {
            Header: 'рецензия',
            accessor: (row) => `${row.review}`,
            Filter: Filter,
            id: 'review'
          },
          {
            Header: 'Акт о внедрении',
            accessor: (row) => (row.implementation_act ? 'да' : 'нет'),
            Filter: Filter,
            id: 'implementationAct'
          },
          {
            Header: 'отчет о плагиате',
            accessor: (row) => (row.plagarism_report ? row.plagarism_report : 'нет'),
            Filter: Filter,
            id: 'plagiarismReport'
          },
          {
            Header: 'заявление на последипломный отпуск',
            accessor: (row) => (row.student.application_for_postgraduate_leave ? row.student.application_for_postgraduate_leave : 'нет'),
            Filter: Filter,
            id: 'postgraduateLeave'
          },
          {
            Header: 'рекомендован к поступлению',
            accessor: (row) => `${row.student.recommended_for_admission}`,
            Filter: Filter,
            id: 'recommendedForAdmission'
          }
        ]
      }
    ],
    []
  );

  const {
    getTableProps,
    getTableBodyProps,
    headerGroups,
    rows,
    prepareRow,
    state,
    setGlobalFilter
  } = useTable(
    {
      columns,
      data: tableData
    },
    useFilters
  );

  const handleDoubleClick = (rowIndex, columnId) => {
    setEditableCell({ rowIndex, columnId });
  };

  const toggleColumnVisibility = (columnId) => {
    if (hiddenColumns.includes(columnId)) {
      setHiddenColumns(hiddenColumns.filter(id => id !== columnId));
    } else {
      setHiddenColumns([...hiddenColumns, columnId]);
    }
  };

  const handleCellChange = (e, rowIndex, columnId) => {
    const { value } = e.target;
    const updatedData = tableData.map((row, index) => {
      if (index === rowIndex) {
        return {
          ...row,
          [columnId]: value
        };
      }
      return row;
    });

    setTableData(updatedData);
  };

  const exportToExcel = () => {
    const visibleColumns = headerGroups[0].headers.filter(column => !hiddenColumns.includes(column.id));
    const headers = visibleColumns.map(column => column.Header);
    const data = rows.map(row =>
      visibleColumns.map(column => {
        const cell = row.cells.find(cell => cell.column.id === column.id);
        return cell ? cell.value : '';
      })
    );

    const wb = XLSX.utils.book_new();
    const ws = XLSX.utils.aoa_to_sheet([headers, ...data]);
    XLSX.utils.book_append_sheet(wb, ws, 'Data');
    XLSX.writeFile(wb, 'Data.xlsx');
  };

  return (
    <div>
      <h4 className="mb-4 text-center">Таблица данных</h4>
      <Button onClick={exportToExcel} className="mb-4">Export to Excel</Button>

      <table {...getTableProps()} id="dataTable" className={styles.huge_table}>
        <thead>
          {headerGroups.map(headerGroup => (
            <tr {...headerGroup.getHeaderGroupProps()}>
              {headerGroup.headers.map(column => (
                <th
                  {...column.getHeaderProps()}
                  className={hiddenColumns.includes(column.id) ? styles.hidden : ''}
                >
                  <div>
                    {column.render('Header')}
                    {column.canFilter && (
                      <Filter column={column} />
                    )}
                  </div>
                  <div>
                    <button onClick={() => toggleColumnVisibility(column.id)}>
                      {hiddenColumns.includes(column.id) ? 'Показать' : 'Скрыть'}
                    </button>
                  </div>
                </th>
              ))}
            </tr>
          ))}
        </thead>

        <tbody {...getTableBodyProps()}>
          {rows.map((row, i) => {
            prepareRow(row);
            return (
              <tr {...row.getRowProps()}>
                {row.cells.map(cell => {
                  const { column } = cell;
                  const isEditable = editableCell && editableCell.rowIndex === i && editableCell.columnId === column.id;

                  return (
                    <td
                      {...cell.getCellProps()}
                      onDoubleClick={() => handleDoubleClick(i, column.id)}
                    >
                      {isEditable ? (
                        <input
                          type="text"
                          value={cell.value}
                          onChange={e => handleCellChange(e, i, column.id)}
                        />
                      ) : (
                        cell.render('Cell')
                      )}
                    </td>
                  );
                })}
              </tr>
            );
          })}
        </tbody>
      </table>
    </div>
  );
}

export default SecretaryHugeTable;
