import React, { Component, useState } from 'react';
import styles from './SideMenu.module.css';

const SideMenu = ({ toggleMenu, isSideMenuOpen }) => {
  console.log("Меню открыто:", isSideMenuOpen);

  return (
    <>
      <div  style={{ display: isSideMenuOpen ? 'block' : 'none' }}>
        <div className={styles.sidebar}>
        <button className={styles.close_btn} onClick={() => { toggleMenu(); }}>×</button>
        <ul>
        <li><Link to='profile_teacher'>Личный кабинет</Link></li>
        <li><Link to='students_list'>Курируемые выпускники</Link></li>
        <li><Link to="view_protection_schedule"> График защит ВКР</Link></li>
        <li><Link to="#"> Инфо </Link></li>
        <li><Link to="#"> Приказы </Link></li>
        </ul>
        </div>
        </div>

      {isSideMenuOpen && <div className={styles.overlay} onClick={toggleMenu}></div>}
    </>
  );
};

export default SideMenu;
