import React, { useState } from 'react';
import { Modal, Button, Form } from 'react-bootstrap';
import { useAsyncDebounce } from 'react-table';

const Filter = ({
  column: { filterValue, setFilter, preFilteredRows, id }
}) => {
  const [isOpen, setIsOpen] = useState(false);

  const toggleModal = () => {
    setIsOpen(!isOpen);
  };

  const applyFilter = (value) => {
    setFilter(value || undefined);
    toggleModal();
  };

  const options = React.useMemo(() => {
    const options = new Set();
    preFilteredRows.forEach(row => {
      options.add(row.values[id]);
    });
    return [...options.values()];
  }, [id, preFilteredRows]);

  const onChange = useAsyncDebounce(value => {
    applyFilter(value);
  }, 200);

  return (
    <>
      <Button onClick={toggleModal}>Filter</Button>
      <Modal show={isOpen} onHide={toggleModal} centered>
        <Modal.Header closeButton>
          <Modal.Title>Filter Options</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form.Control as="select" value={filterValue} onChange={e => onChange(e.target.value)}>
            <option value="">All</option>
            {options.map((option, index) => (
              <option key={index} value={option}>
                {option}
              </option>
            ))}
          </Form.Control>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={toggleModal}>Close</Button>
          <Button variant="primary" onClick={() => applyFilter('')}>Clear</Button>
        </Modal.Footer>
      </Modal>
    </>
  );
};

export default Filter;
