import React, { Component } from 'react';
import BaseModal from '../BaseModal/BaseModal';
import { Row, Col, Button } from 'react-bootstrap';
import styles_base from '../BaseModal/BaseModal.module.css';
import ScheduleFQWModal from '../ScheduleFQWModal/ScheduleFQWModal';
import {Link} from 'react-router-dom'

export default class ModalViewGraphExists extends Component {
  constructor(props) {
    super(props);
    this.state = {
      showScheduleModal: false
    };
  }

  handleScheduleModalOpen = () => {
    this.setState({ showScheduleModal: true });
  }

  handleScheduleModalClose = () => {
    this.setState({ showScheduleModal: false });
  }

  render() {
    const { show, handleClose, darkMode, schedule_stud_prof, request_data_task, teacherData } = this.props;
    const { showScheduleModal } = this.state;
    console.log(teacherData)
    const content = (
      schedule_stud_prof && schedule_stud_prof.length > 0
        ? 'График был создан'
        : 'График работы над ВКР еще не был создан'
    );

    const footer = (
      schedule_stud_prof && schedule_stud_prof.length > 0
        ? (
          <Row className="justify-content-between px-3">
            <Col md={5}>
              <Button variant='none' onClick={this.handleScheduleModalOpen} className={styles_base.button_cancel}>Просмотреть</Button>
            </Col>
            <Col md={5}>
              <Button variant='none' className={styles_base.button_next} onClick={handleClose}>Отмена</Button>
            </Col>
          </Row>
        )
        : (
          <Row className="justify-content-between px-3">
            <Col>
            <Link to='/create_schedule_work_fqw'>
          <Button variant='none' className={styles_base.button_next} onClick={handleClose}>Редактировать</Button>
          </Link>
          </Col>
            <Col md={5}>
              <Button variant='none' className={styles_base.button_next} onClick={handleClose}>ОК</Button>
            </Col>
            
          </Row>
        )
    );

    return (
      <>
        <BaseModal
          show={show}
          handleClose={handleClose}
          title={'График работы над ВКР'}
          content={content}
          darkMode={darkMode}
          sourceData={schedule_stud_prof}
          footer={footer}
          request_data_task={request_data_task}
          data={teacherData}
        />
        <ScheduleFQWModal
          show={showScheduleModal}
          handleClose={this.handleScheduleModalClose}
          darkMode={false}
          schedule_stud_prof={schedule_stud_prof}
          request_data_task={request_data_task}
          data={teacherData}
        />
      </>
    );
  }
}
