import React, { Component } from 'react';
import Header from '../components/Header/Header.js';
import Search from '../components/ProfileBlocks/Search/Search.js';
import Background from '../components/Background/Background.js';
import Footer from '../components/Footer/Footer.js';
import ProfileBlocks from '../components/ProfileBlocks/Student/ProfileBlocks.js';
import SideMenu from '../components/SideMenu/SideMenu.js';
import styles from './SideMenu.module.css';

export default class ProfileStudent extends Component {
  constructor(props) {
    super(props);
    this.state = {
      isAuthenticated: true,
      isSideMenuOpen: false
    };
  }

  toggleMenu = () => {
    this.setState(prevState => ({ isSideMenuOpen: !prevState.isSideMenuOpen }));
  };

  render() {
    const { isAuthenticated, isSideMenuOpen } = this.state; // Добавляем isSideMenuOpen в состояние
    return (
      <Background>
        <Header isAuthenticated={isAuthenticated} toggleMenu={this.toggleMenu} isSideMenuOpen={isSideMenuOpen} /> {/* Передаем isSideMenuOpen */}
        <SideMenu toggleMenu={this.toggleMenu} isSideMenuOpen={this.state.isSideMenuOpen} />
        <Search />
        <ProfileBlocks />
        <Footer />
      </Background>
    );
  }
}
