import React, { Component } from 'react'
import BaseModal from '../BaseModal/BaseModal'
import { Table, Col, Row, Button, Form } from 'react-bootstrap';
import styles from './ScheduleFQWModal.module.css'
import styles_base from '../BaseModal/BaseModal.module.css'
import generateTask from './GenerateTask';

export default class ScheduleFQWModal extends Component {
  
  constructor(props) {
    super(props);
    
    this.state = {
      editedStages: this.props.schedule_stud_prof.map(stage => ({
        ...stage,
        completion_mark: stage.completion_mark || '',
        absence: stage.absence || ''
      })),
      isEditing: false, // State to track if editing is enabled
      editedRowIndex: null // State to track the index of the currently edited row
    };
  }

  handleClick = async () => {
    await generateTask({
      request_data_task: this.props.request_data_task,
      schedule_stud_prof: this.state.editedStages, 
      data: this.props.data
    });
  };

  handleInputChange = (index, field, value) => {
    const updatedStages = [...this.state.editedStages];
    updatedStages[index][field] = value === 'true';
    this.setState({ editedStages: updatedStages });
  };

  handleDoubleClick = (index) => {
    // Enable editing mode and set the index of the edited row
    this.setState({ isEditing: true, editedRowIndex: index });
  };

  handleBlur = () => {
    // Disable editing mode on blur
    this.setState({ isEditing: false, editedRowIndex: null });
  };

  render() {
    const { show, handleClose, darkMode, request_data_task, data } = this.props;
    const { editedStages, isEditing, editedRowIndex } = this.state;

    const content = (
      <div className='p-2'>
        <Table bordered hover>
          <thead>
            <tr>
              <th>Этап</th>
              <th>Начало</th>
              <th>Конец</th>
              <th>Результат</th>
              <th>Отметка о выполнении этапа</th>
              <th>Неявка</th>
            </tr>
          </thead>
          <tbody>
            {editedStages.map((stage, index) => (
              <tr key={stage.id}>
                <td>{stage.stage_name}</td>
                <td>{stage.start}</td>
                <td>{stage.end}</td>
                <td>{stage.result}</td>
                <td>
                  {isEditing && editedRowIndex === index && data.type_of_user === 'руководитель' ? (
                    <Form.Control
                      as="select"
                      value={stage.completion_mark}
                      onChange={(e) => this.handleInputChange(index, 'completion_mark', e.target.value)}
                      onBlur={this.handleBlur}
                    >
                      <option value="true">true</option>
                      <option value="false">false</option>
                    </Form.Control>
                  ) : (
                    stage.completion_mark.toString()
                  )}
                </td>
                <td onDoubleClick={() => this.handleDoubleClick(index)}>
                  {isEditing && editedRowIndex === index && data.type_of_user === 'руководитель' ? (
                    <Form.Control
                      as="select"
                      value={stage.absence}
                      onChange={(e) => this.handleInputChange(index, 'absence', e.target.value)}
                      onBlur={this.handleBlur}
                    >
                      <option value="true">true</option>
                      <option value="false">false</option>
                    </Form.Control>
                  ) : (
                    stage.absence.toString()
                  )}
                </td>
              </tr>
            ))}
          </tbody>
        </Table>
      </div>
    );

    const footer = (
      <Row className="justify-content-between px-3">
        <Col md={5}>
          <Button variant='none' className={styles_base.button_cancel} onClick={this.handleClick}>Экспортировать</Button>
        </Col>
        <Col md={5}>
          <Button variant='none' className={styles_base.button_next} onClick={handleClose}>ОКЭЙ</Button>
        </Col>
      </Row>
    );

    return (
      <BaseModal
        show={show}
        handleClose={handleClose}
        title={'График работы над ВКР'}
        content={content}
        darkMode={darkMode}
        sourceData={this.state.editedStages}
        modalClass={styles.container_modal}
        footer={footer}
        request_data_task={request_data_task}
      >
      </BaseModal>
    );
  }
}
