import React, { useState, useMemo, useEffect } from 'react';
import { useTable, useFilters } from 'react-table';
import * as XLSX from 'xlsx';
import { saveAs } from 'file-saver';
import { Button, Row, Col } from 'react-bootstrap';
import styles from '../../../container.module.css';
import Filter from '../Filter/Filter';
import hide_cell from '../../../img/hide_cell.png';
import BlockInfo from '../../BlockInfo/BlockInfo';

function SecretaryHugeTable({ info_for_table }) {

  const isFirstRowEmpty = (data) => {
    return data.length > 0 && Object.values(data[0]).every((cell) => cell === null || cell === '');
  };

  const [tableData, setTableData] = useState(() => {
    // Remove the first row if it is empty
    const data = info_for_table.requests;
    if (isFirstRowEmpty(data)) {
      return data.slice(1);
    }
    return data;
  });

  const [originalData] = useState(info_for_table.requests); // Store the original data
  const [editableCell, setEditableCell] = useState(null);
  const [hiddenColumns, setHiddenColumns] = useState([]);
  const [isColumnsModified, setIsColumnsModified] = useState(false);

  const columns = useMemo(() => [
    {
      Header: '№ п/п',
      accessor: (row, index) => index + 1,
      Filter: Filter
    },
    {
      Header: 'ср.б.',
      accessor: (row) =>  `${row.student.average_score}`,
      Filter: Filter
    },
    {
      Header: 'номер протокола',
      accessor: (row) =>  `${row.workProtecton.protocol_number}`,
      Filter: Filter
    },
    {
      Header: 'дата протокола',
      accessor: (row) =>  `${row.workProtecton.date}`,
      Filter: Filter
    },
    {
      Header: 'Фамилия, Имя, Отчество',
      accessor: (row) => `${row.student.surname} ${row.student.name} ${row.student.patronymic}`,
      Filter: Filter,
    },
    {
      Header: 'Тема',
      accessor:  (row) =>  `${row.theme.name}`,
      Filter: Filter,
    },
    {
      Header: 'Руководитель',
      accessor: (row) => `${row.teacher.surname} ${row.teacher.name} ${row.teacher.patronymic}`,
      Filter: Filter,
    },
    {
      Header: 'Консультант',
      accessor: (row) => `${row.consultant.surname} ${row.consultant.name} ${row.consultant.patronymic}`,
      Filter: Filter,
    },
    {
      Header: 'Рецензент',
      accessor: (row) =>  `${row.consultant.surname} ${row.consultant.name} ${row.consultant.patronymic}`,
      Filter: Filter,
    },
    {
      Header: 'организация',
      accessor: (row) =>  `${row.theme.company.name} ${row.theme.company.last_name_of_responsible} ${row.theme.company.first_name_of_responsible} ${row.theme.company.patronymic_name_of_responsible}
       ${row.theme.company.job_title_of_resposible} ${row.theme.company.additional_information}`,
       Filter: Filter,
    },
    {
      Header: 'номер зачетки',
      accessor: (row) =>  `${row.student.record_book_number}`,
       Filter: Filter,
    },
    {
      Header: 'Телефон',
      accessor: (row) =>  `${row.student.phone}`,
      Filter: Filter,
    },
    {
      Header: 'Email',
      accessor: (row) =>  `${row.student.email}`,
      Filter: Filter,
    },
    {
      Header: 'бюджет/платный',
      accessor: (row) =>  `${row.student.basis_of_study}`,
      Filter: Filter,
    },
    {
      Header: 'оценка на предзащите/ преддипломная практика',
      accessor: (row) =>  `${row.preprotection_grade}`,
      Filter: Filter,
    },
    {
      Header: 'оценка на защите',
      accessor: (row) =>  `${row.workProtecton.final_grade}`,
      Filter: Filter,
    },
    {
      Header: 'дата рождения',
      accessor: (row) =>  `${row.student.date_of_birth}`,
      Filter: Filter,
    },
    {
      Header: 'год окончания предыдущ образования',
      accessor: (row) =>  `${row.student.year_of_prev_education_completion}`,
      Filter: Filter,
    },
    {
      Header: 'форма предыдущего образования',
      accessor: (row) =>  `${row.student.form_of_prev_education}`,
      Filter: Filter,
    },
    {
      Header: 'год поступления',
      accessor: (row) =>  `${row.student.year_of_admission}`,
      Filter: Filter,
    },
    {
      Header: 'особые отметки',
      accessor: (row) =>  `${row.student.special_marks}`,
      Filter: Filter,
    },
    {
      Header: 'приказы',
      columns: [
        {
          Header: 'допуск',
          accessor: (row) => `${row.workProtecton.final_grade}`,
          Filter: Filter,
          isVisible: true
        },
        {
          Header: 'тема',
          accessor: (row) => `${row.workProtecton.final_grade}`,
          Filter: Filter,
          isVisible: true
        },
        {
          Header: 'изменение темы',
          accessor: (row) => `${row.workProtecton.final_grade}`,
          Filter: Filter,
          isVisible: true
        },
        {
          Header: 'рецензент',
          accessor: (row) => `${row.workProtecton.final_grade}`,
          Filter: Filter,
          isVisible: true
        },
        {
          Header: 'отчисление',
          accessor: (row) => `${row.workProtecton.final_grade}`,
          Filter: Filter,
          isVisible: true
        },
        {
          Header: 'с какого числа отчисление',
          accessor: (row) => `${row.workProtecton.final_grade}`,
          Filter: Filter,
          isVisible: true
        }
      ]
    },
    {
      Header: 'отметки о выполнении',
      columns: [
        {
          Header: 'долги',
          accessor: (row) =>  `${row.student.number_of_debts}`,
          Filter: Filter
        },
        {
          Header: 'справка о выполнении уч. плана',
          accessor: (row) =>  `${row.student.certificate_of_curriculum_completion}`,
          Filter: Filter
        },
        {
          Header: 'сдана зачетка',
          accessor: (row) =>  `${row.student.record_book_submitted}`,
          Filter: Filter
        },
        {
          Header: 'сдана пояснительная записка',
          accessor: (row) => (row.explanatory_note_submitted ? 'да' : 'нет'),
          Filter: Filter
        },
        {
          Header: 'красный диплом',
          accessor: (row) => (row.student.diploma_with_honors ? 'да' : 'нет'),
          Filter: Filter
        },
        {
          Header: 'согласие на публикацию',
          accessor: (row) => (row.publication_agree ? 'да' : 'нет'),
          Filter: Filter
        },
        {
          Header: 'отзыв',
          accessor: (row) =>  `${row.teacher_review}`,
          Filter: Filter
        },
        {
          Header: 'рецензия',
          accessor: (row) =>  `${row.review}`,
          Filter: Filter
        },
        {
          Header: 'Акт о внедрении',
          accessor: (row) => (row.implementation_act ? 'да' : 'нет'),
          Filter: Filter
        },
        {
          Header: 'отчет о плагиате',
          accessor: (row) => (row.plagarism_report ? row.plagarism_report : 'нет'),
          Filter: Filter
        },
        {
          Header: 'заявление на последипломный отпуск',
          accessor: (row) => (row.student.application_for_postgraduate_leave ? row.student.application_for_postgraduate_leave: 'нет'),
          Filter: Filter
        },
        {
          Header: 'рекомендован к поступлению',
          accessor: (row) =>  `${row.student.recommended_for_admission}`,
          Filter: Filter
        },
      ]
    },
    {
      Header: 'вид ВКР',
      accessor: (row) =>  `${row.type_of_fqw}`,
      Filter: Filter,
      headerProps: { rowSpan: 2 },
    },
    {
      Header: 'ВКР по заявке',
      accessor: (row) =>  `${row.theme.fqw_by_application}`,
      Filter: Filter,
    },
    {
      Header: 'ВКР на англ. языке',
      accessor: (row) => (row.fqw_in_english ? 'да' : 'нет'),
      Filter: Filter,
    },
  ], [tableData]);

  useEffect(() => {
    setHiddenColumns(Object.keys(columns).filter(columnKey => !columns[columnKey].isVisible));
  }, [columns]);

  const data = useMemo(() => tableData, [tableData]);

  const tableInstance = useTable({ columns, data }, useFilters);

  const handleExport = () => {
    const headers = columns.map(col => col.Header);
    const rows = tableData.map(row => Object.values(row));
    const worksheet = XLSX.utils.aoa_to_sheet([headers, ...rows]);
    const workbook = XLSX.utils.book_new();
    XLSX.utils.book_append_sheet(workbook, worksheet, 'Sheet1');
    const excelBuffer = XLSX.write(workbook, { bookType: 'xlsx', type: 'array' });
    const excelBlob = new Blob([excelBuffer], { type: 'application/octet-stream' });
    saveAs(excelBlob, 'exported_table.xlsx');
  };

  const handleDoubleClick = (rowIndex, columnId) => {
    setEditableCell({ rowIndex, columnId });
  };

  const handleChange = (e, rowIndex, columnId) => {
    const newValue = e.target.value;
    setTableData((prevData) =>
      prevData.map((row, index) =>
        index === rowIndex ? { ...row, [columnId]: newValue } : row
      )
    );
  };

  const handleBlur = () => {
    setEditableCell(null);
  };

  const handleKeyDown = (e, rowIndex, columnId) => {
    if (e.key === 'Enter') {
      setEditableCell(null);
    }
  };

  const { getTableProps, getTableBodyProps, headerGroups, rows, prepareRow, setFilter } = tableInstance;

  return (
    <>
      <BlockInfo header='Защита ВКР'>
        <Button variant='success' onClick={handleExport} style={{ marginRight: '20px' }}>Экспортировать таблицу</Button>
      </BlockInfo>
      <table {...getTableProps()} className={styles.table}>
        <thead>
          {headerGroups.map(headerGroup => (
            <tr {...headerGroup.getHeaderGroupProps()}>
              {headerGroup.headers.map(column => (
                <th {...column.getHeaderProps()}>
                  {column.render('Header')}
                  <div>{column.canFilter ? column.render('Filter') : null}</div>
                </th>
              ))}
            </tr>
          ))}
        </thead>
        <tbody {...getTableBodyProps()}>
          {rows.map((row, rowIndex) => {
            prepareRow(row);
            return (
              <tr {...row.getRowProps()}>
                {row.cells.map(cell => {
                  return (
                    <td
                      {...cell.getCellProps()}
                      onDoubleClick={() => handleDoubleClick(rowIndex, cell.column.id)}
                    >
                      {editableCell && editableCell.rowIndex === rowIndex && editableCell.columnId === cell.column.id ? (
                        <input
                          value={cell.value}
                          onChange={(e) => handleChange(e, rowIndex, cell.column.id)}
                          onBlur={handleBlur}
                          onKeyDown={(e) => handleKeyDown(e, rowIndex, cell.column.id)}
                          autoFocus
                        />
                      ) : (
                        cell.render('Cell')
                      )}
                    </td>
                  );
                })}
              </tr>
            );
          })}
        </tbody>
      </table>
    </>
  );
}

export default SecretaryHugeTable;
