import React, { Component, useState } from 'react'
import styles from './SideMenu.module.css';

const SideMenu = ({ toggleMenu }) => {
  const [isOpen, setIsOpen] = useState(false);

  return (
    <>
      <div className={`${styles.sidebar} ${isOpen ? 'open' : ''}`}>
        <button className="close-btn" onClick={() => { setIsOpen(!isOpen); toggleMenu(); }}>×</button>
        {/* Контент бокового меню */}
      </div>
      {isOpen && <div className={styles.overlay} onClick={() => { setIsOpen(!isOpen); toggleMenu(); }}></div>}
    </>
  );
};

export default SideMenu;
