import React, { useState } from 'react';
import { Table, Col, Row, Button, Form } from 'react-bootstrap';
import generateProtocolsFunction from './GenerateProtocolFunction';

function GenerateProtocols({ composition_of_sec, groups_for_schedule, requestAndWorkProtection, data }) {
  const [selectedGroup, setSelectedGroup] = useState(groups_for_schedule[0].name);

  const handleClick = async () => {
    // Фильтрация данных по выбранной группе
    const groupData = requestAndWorkProtection.filter(item => item.request.student.group.name === selectedGroup);
  
    await generateProtocolsFunction({
      composition_of_sec: composition_of_sec,
      selectedGroup: selectedGroup,
      groupData: groupData, // Передача отфильтрованных данных
      data: data
    });
  };
  

  // Count the number of requests per group
  const countRequestsPerGroup = () => {
    const groupCounts = {};
    requestAndWorkProtection.forEach(item => {
      const groupName = item.request.student.group.name;
      if (groupCounts[groupName]) {
        groupCounts[groupName]++;
      } else {
        groupCounts[groupName] = 1;
      }
    });
    return groupCounts;
  };

  const groupCounts = countRequestsPerGroup();

  return (
    <div>
      <h4 className='text-center'>Генерация протоколов</h4>
      <Form>
        <Form.Group controlId="groupSelect">
          <Form.Label>Выберите группу</Form.Label>
          <Form.Control as="select" value={selectedGroup} onChange={(e) => setSelectedGroup(e.target.value)}>
            {groups_for_schedule.map(group => (
              <option key={group.id} value={group.name}>
                {group.name} ({groupCounts[group.name] || 0} запросов)
              </option>
            ))}
          </Form.Control>
        </Form.Group>
        <Button variant='none' onClick={handleClick}>Создать протокол</Button>
      </Form>
    </div>
  );
}

export default GenerateProtocols;
