import React from 'react';
import { Table } from 'react-bootstrap';
import styles from './Protection.module.css';

const ViewProtectionSchedulePersons = ({ protection_schedule, students_list_for_schedule }) => {

  const getFullName = (studentData) => {
    if (studentData && studentData.user) {
      const { last_name, first_name, patronymic } = studentData.user;
      return `${last_name} ${first_name} ${patronymic}`;
    } else {
      return 'Unknown';
    }
  };

  const distributeStudentsByProtectionDays = (schedule) => {
    const sortedSchedule = [...schedule].sort((a, b) => {
      return new Date(a.date) - new Date(b.date);
    });
  
    const groupedSchedule = {};
  
    const usedStudents = new Set();
  
    sortedSchedule.forEach((item) => {
      if (!item.group) return;
  
      const dateKey = item.date;
      const groupName = item.group.name;
      const groupId = item.group.id;
  
      if (!groupedSchedule[dateKey]) {
        groupedSchedule[dateKey] = {};
      }
  
      if (!groupedSchedule[dateKey][groupName]) {
        groupedSchedule[dateKey][groupName] = { students: [] };
      }
  
      const students = students_list_for_schedule.filter((student) => student.group.id === groupId);
      const availableStudents = students.filter((student) => !usedStudents.has(student.user.username));
      const studentsToAdd = availableStudents.slice(0, 12);
  
      studentsToAdd.forEach((student) => {
        const matchingRequest = item.request_list.find((req) => req.id === student.request);
        const personalTime = matchingRequest ? matchingRequest.personal_time : '';
        const additionalInfo = matchingRequest ? matchingRequest.additional_information : '';
  
        groupedSchedule[dateKey][groupName].students.push({
          ...student,
          personal_time: personalTime,
          additional_information: additionalInfo,
          vkr_theme: matchingRequest ? matchingRequest.theme.name : 'Unknown',
        });
  
        usedStudents.add(student.user.username);
      });
    });
  
    return groupedSchedule;
  };
  
  

  const groupedSchedule = distributeStudentsByProtectionDays(protection_schedule);

  return (
    <div>
      <h4 className='text-center'> График защит ВКР по фамильный</h4>
      <div className='mx-md-5'>
        {Object.entries(groupedSchedule).map(([date, groups]) => (
          <div key={date}>
            <h3>{date}</h3>
            {Object.entries(groups).map(([group, { students }]) => (
              <React.Fragment key={`${date}-${group}`}>
                {students.length > 0 && (
                  <Table bordered hover responsive className={styles.protectionTable}>
                    <thead>
                      <tr>
                        <th>ФИО</th>
                        <th>Группа</th>
                        <th>Дата</th>
                      </tr>
                    </thead>
                    <tbody>
                      {students.map((student, index) => (
                        <tr key={index}>
                          <td>{getFullName(student)}</td>
                          <td>{group}</td>
                          <td>{date}</td>
                        </tr>
                      ))}
                    </tbody>
                  </Table>
                )}
              </React.Fragment>
            ))}
          </div>
        ))}
      </div>
    </div>
  );
};

export default ViewProtectionSchedulePersons;
