import React, { useState, useRef } from 'react';
import BlockInfo from '../../BlockInfo/BlockInfo';
import styles from './PublishOrdersForm.module.css'

function PublishOrdersForm() {
  const [dragging, setDragging] = useState(false);
  const [selectedFiles, setSelectedFiles] = useState([]);
  const inputRef = useRef(null);

  const handleDragStart = (event) => {
    event.preventDefault();
    setDragging(true);
  };

  const handleDragEnd = (event) => {
    event.preventDefault();
    setDragging(false);
  };

  const handleDrop = (event) => {
    event.preventDefault();
    setDragging(false);
    const data = event.dataTransfer.getData('text/plain');
    console.log('Dropped data:', data);
    setSelectedFiles([...selectedFiles, data]); // Добавляем выбранный файл к списку
  };

  const handleFileInputChange = (event) => {
    const files = Array.from(event.target.files);
    console.log('Selected files:', files);
    setSelectedFiles([...selectedFiles, ...files.map(file => file.name)]); // Добавляем выбранные файлы к списку
  };

  return (
    <>
      <h4 className="mb-4 text-center">Загрузка приказов</h4>
      <BlockInfo>
        Загрузите приказы в формате pdf и они станут доступны всем пользователям в системе
      </BlockInfo>
      <div
        onDragOver={(event) => event.preventDefault()}
        onDrop={handleDrop}
      >
        <div
          draggable
          onDragStart={handleDragStart}
          onDragEnd={handleDragEnd}
          className={styles.form_drag}
          onClick={() => inputRef.current.click()}
        >
          Drag me!
        </div>
      </div>
      <input
        type="file"
        accept=".pdf"
        ref={inputRef}
        style={{ display: 'none' }}
        onChange={handleFileInputChange}
        multiple // Разрешаем выбирать несколько файлов
      />
      {selectedFiles.length > 0 && (
        <div>
          <h5>Выбранные файлы:</h5>
          <ul>
            {selectedFiles.map((fileName, index) => (
              <li key={index}>{fileName}</li>
            ))}
          </ul>
        </div>
      )}
    </>
  );
}

export default PublishOrdersForm;
