import React, { useState } from 'react';
import { Container, Card, Row, Col, Form, Button } from 'react-bootstrap';
import prev from '../../../img/prev.png'; 
import next from '../../../img/next.png';
import styles from '../FormsBase.module.css';

const MarksCompletionForm = () => {
  const [currentStep, setCurrentStep] = useState(0);

  const nextStep = () => {
    setCurrentStep(currentStep + 1);
  };

  const prevStep = () => {
    setCurrentStep(currentStep - 1);
  };

  return (

      <Row>
        <Col md={7}>
          <h4 className="mb-4 text-center">Форма</h4>
          {currentStep === 0 && (
            <div className={`step ${currentStep === 0 ? 'active' : ''}`}>
              <Form.Group as={Row}>
                <Form.Label column sm={5} title="Количественное значение, если нет - 0">Долги</Form.Label>
                <Col sm={7}>
                  <Form.Control type="text" placeholder="количество долгов" />
                </Col>
              </Form.Group>

              <Form.Group as={Row} data-toggle="tooltip" title="Если справка сдана, укажите дату. Если не сдана, оставьте поле пустым">
                <Form.Label column sm={5}>Справка о выполнении уч.плана</Form.Label>
                <Col sm={7}>
                  <Form.Control type="date" placeholder="дата справки" />
                </Col>
              </Form.Group>

              <Row className="form-group">
              <Form.Label column sm={5} htmlFor="gradebook">Сдана зачетка</Form.Label>
              <Col sm={7} className="d-flex align-items-center" id="gradebook">
                <Form.Check inline type="radio" id="gradebook-yes" name="gradebook" label="да" value="yes" className="mr-2" />
                <Form.Check inline type="radio" id="gradebook-no" name="gradebook" label="нет" value="no" className="ml-2" />
              </Col>
            </Row>

            <Row className="form-group">
              <Form.Label column sm={5} htmlFor="prestigious_diploma">Красный диплом</Form.Label>
              <Col sm={7} className="d-flex align-items-center" id="prestigious_diploma">
                <Form.Check inline type="radio" id="prestigious_diploma-yes" name="prestigious_diploma" label="да" value="yes" className="mr-2" />
                <Form.Check inline type="radio" id="prestigious_diploma-no" name="prestigious_diploma" label="нет" value="no" className="ml-2" />
              </Col>
            </Row>

            <Row className="form-group">
              <Form.Label column sm={5} htmlFor="consent_to_publication">Согласие на публикацию</Form.Label>
              <Col sm={7} className="d-flex align-items-center" id="consent_to_publication">
                <Form.Check inline type="radio" id="consent_to_publication-yes" name="consent_to_publication" label="да" value="yes" className="mr-2" />
                <Form.Check inline type="radio" id="consent_to_publication-no" name="consent_to_publication" label="нет" value="no" className="ml-2" />
              </Col>
            </Row>

              
              <Button variant="none" className={styles.next} onClick={nextStep}>
                <img src={next} width="10%" alt="Следующий" />
              </Button>
            </div>
          )}
          {currentStep === 1 && (
            <div className={`step ${currentStep === 1 ? 'active' : ''}`}>
              {/* Add form fields for step 2 here */}
              вапкпенрглшднекыукрве
              <Button variant="none" className={styles.prev} onClick={prevStep}>
                <img src={prev} width="20%" alt="Предыдущий" />
              </Button>
              <Button type="submit" className="button-form">Отправить</Button>
            </div>
          )}
        </Col>
        <Col md={4} className="mx-3">
          <Card className="mt-4">
            <Card.Body>
              <h4 className="mb-4 text-center">Правила заполнения формы</h4>
              <p>
                Данная форма доступна для подачи данных о выполнении отметок образовательного процесса вплоть до того
                момента, как будет проверена секретарем ГЭК. Далее выпускник не сможет изменить данные об отметках.
                Заполняйте данную форму внимательно. Если сомневаетесь в правильности заполнения полей, оставьте их пустыми.
              </p>
            </Card.Body>
          </Card>
        </Col>
      </Row>
  
  );
};

export default MarksCompletionForm;
