import React from 'react';
import {Table} from 'react-bootstrap'

function ThemesListReading({ themes_list }) {
  return (
    <div>
      <h4 className='text-center'>Список тем</h4>
      <div className='mx-5 mt-4'>
      <Table bordered hover>
        <thead>
          <tr>
            <th>ID</th>
            <th>Название</th>
            <th>Преподаватель</th>
            <th>Организация</th>
            <th>Компания</th>
          </tr>
        </thead>
        <tbody>
          {themes_list.map((theme) => (
            <tr key={theme.id}>
              <td>{theme.id}</td>
              <td>{theme.name}</td>
              <td>
                
              {theme.teacher.user.id !== null ?
    `${theme.teacher.user.last_name} ${theme.teacher.user.first_name} ${theme.teacher.user.patronymic}` :
    'кафедра сии'
}


              </td>
              <td>{theme.department ? 'Да' : 'Нет'}</td>
              <td>
                {theme.company.name && (
                  <>
                    Название: {theme.company.name}
                    Фамилия ответственного: {theme.company.last_name_of_responsible}
                    Имя ответственного: {theme.company.first_name_of_responsible}
                    Отчество ответственного: {theme.company.patronymic_of_responsible}
                    Должность ответственного: {theme.company.job_title_of_resposible}
                    Дополнительная информация: {theme.company.additional_information}
                  </>
                )}
              </td>
            </tr>
          ))}
        </tbody>
      </Table>
    </div>
    </div>
  );
}

export default ThemesListReading;
