import React, { useState } from 'react';
import { Container, Row, Col, Form, Button } from 'react-bootstrap';
import styles from '../../ProfileBlocks.module.css';
import reveal from '../../../../img/reveal.png';
import ModalRequests from '../../../Modals/ModalRequests/ModalRequests';
import RequestsContent from './RequestsContent';

const RequestsCard = ({ requestdata }) => {
  const [currentRequestIndex, setCurrentRequestIndex] = useState(0);
  const [showModal, setShowModal] = useState(false);

  // Фильтруем заявки по двум условиям
  const visibleRequests = requestdata.filter(request =>
    request.is_accepted_request === null ||
    (request.is_accepted_request === true && request.is_accepted_theme === false)
  );

  const requestsCount = visibleRequests.length; // Количество заявок по условиям

  const handleAccept = () => {
    setCurrentRequestIndex(prevIndex => prevIndex + 1);
  };

  const handleDecline = () => {
    setCurrentRequestIndex(prevIndex => prevIndex + 1);
  };

  const currentRequest = visibleRequests[currentRequestIndex];
  const noRequestsLeft = !currentRequest || currentRequestIndex >= visibleRequests.length;

  const showNextRequest = () => {
    if (currentRequestIndex < visibleRequests.length - 1) {
      setCurrentRequestIndex(prevIndex => prevIndex + 1);
    }
  };

  const handleCloseModal = () => {
    setShowModal(false);
    showNextRequest();
  };

  return (
    <div className={styles.card}>
      <p className="text-center px-1 py-2 fw-bold">Текущие заявки <span className={styles.badge}>{requestsCount}</span>
        <img src={reveal} className="mx-3" width="20" height="20" onClick={() => setShowModal(true)} alt="reveal" />
      </p>

      {noRequestsLeft ? (
        <p className="text-center">Вам еще не были отправлены заявки</p>
      ) : (
        <div className={styles.request_field}>
          {currentRequest.is_accepted_request === null ? (
            <h6>Заявка на курирование ВКР</h6>
          ) : (
            currentRequest.is_accepted_request === true && currentRequest.is_accepted_theme === false ? (
              <h6>Заявка на смену темы ВКР</h6>
            ) : null
          )}
          <RequestsContent
            currentRequest={currentRequest}
            handleAccept={handleAccept}
            handleDecline={handleDecline}
          />
        </div>
      )}

      <ModalRequests
        show={showModal}
        handleClose={handleCloseModal}
        requestdata={visibleRequests} // Передаем отфильтрованные заявки
      />
    </div>
  );
}

export default RequestsCard;
