import React from 'react';

function ViewProtectionSchedulePersons({ protection_schedule, students_list_for_schedule }) {
    // Функция для форматирования даты
    const formatDate = (dateString) => {
        const date = new Date(dateString);
        const options = { day: 'numeric', month: 'numeric', year: 'numeric' };
        return date.toLocaleDateString('ru-RU', options);
    };

    // Группировка расписания по дням и фамилиям
    const groupedSchedule = protection_schedule.reduce((acc, item) => {
        const dateKey = formatDate(item.date);
        if (!acc[dateKey]) {
            acc[dateKey] = {};
        }
        const student = students_list_for_schedule.find(student => student.student?.group?.id === item.group?.id);
        const lastName = student?.student?.last_name || 'Additional Information';
        if (!acc[dateKey][lastName]) {
            acc[dateKey][lastName] = [];
        }
        acc[dateKey][lastName].push(item);
        return acc;
    }, {});

    return (
        <div>
            {Object.entries(groupedSchedule).map(([date, items]) => (
                <div key={date}>
                    <h3>{date}</h3>
                    {Object.entries(items).map(([lastName, groups]) => (
                        <div key={lastName}>
                            <h4>{lastName}</h4>
                            <ul>
                                {groups.map((group, index) => (
                                    <li key={index}>
                                        {group.group?.name} - {group.additional_information || group.venue_number}
                                    </li>
                                ))}
                            </ul>
                        </div>
                    ))}
                </div>
            ))}
        </div>
    );
}

export default ViewProtectionSchedulePersons;
