import React from 'react';
import { Table } from 'react-bootstrap';
import styles from './Protection.module.css';

const ViewProtectionSchedulePersons = ({ general_work_protection_list, personal_work_protection_list, student_list }) => {

  const getFullName = (studentData) => {
    if (studentData && studentData.user) {
      const { last_name, first_name, patronymic } = studentData.user;
      return `${last_name} ${first_name} ${patronymic}`;
    } else {
      return 'Unknown';
    }
  };

  // Функция для получения времени защиты по идентификатору студента
  const getProtectionTime = (studentId) => {
    const personalWork = personal_work_protection_list.find(item => item.work_protection === studentId);
    return personalWork ? personalWork.personal_time : 'Не указано';
  };

  // Функция для получения темы ВКР по идентификатору студента
  const getVKRTheme = (studentId) => {
    const request = student_list.find(item => item.id === studentId);
    return request ? request.request_list.name : 'Не указана';
  };

  // Группировка студентов по дате и группе
  const distributeStudentsByProtectionDays = (schedule) => {
    const sortedSchedule = [...schedule].sort((a, b) => {
      // Сортируем по дате в возрастающем порядке
      return new Date(a.date) - new Date(b.date);
    });
  
    const groupedSchedule = {};
  
    sortedSchedule.forEach((item) => {
      if (!item.group) return;
  
      const dateKey = item.date;
      const groupName = item.group.name;
  
      if (!groupedSchedule[dateKey]) {
        groupedSchedule[dateKey] = {};
      }
  
      if (!groupedSchedule[dateKey][groupName]) {
        groupedSchedule[dateKey][groupName] = { students: [] };
      }
  
      const students = student_list.filter((student) => student.group.id === item.group.id);
      const studentsToAdd = students.slice(0, 12);
  
      groupedSchedule[dateKey][groupName].students.push(...studentsToAdd.map(student => ({
        ...student,
        protection_time: getProtectionTime(student.id), // Добавляем время защиты
        vkr_theme: getVKRTheme(student.id) // Добавляем тему ВКР
      })));
    });
  
    return groupedSchedule;
  };
  
  const groupedSchedule = distributeStudentsByProtectionDays(general_work_protection_list);

  return (
    <div>
      <h4 className='text-center'> График защит ВКР по фамильный</h4>
      <div className='mx-md-5'>
        {Object.entries(groupedSchedule).map(([date, groups]) => (
          <div key={date}>
            <h3>{date}</h3>
            {Object.entries(groups).map(([group, { students }]) => (
              <React.Fragment key={`${date}-${group}`}>
                {students.length > 0 && (
                  <Table bordered hover responsive className={styles.protectionTable}>
                    <thead>
                      <tr>
                        <th>ФИО</th>
                        <th>Группа</th>
                        <th>Дата</th>
                        <th>Время защиты</th>
                        <th>Тема ВКР</th>
                      </tr>
                    </thead>
                    <tbody>
                      {students.map((student, index) => (
                        <tr key={index}>
                          <td>{getFullName(student)}</td>
                          <td>{group}</td>
                          <td>{date}</td>
                          <td>{student.protection_time}</td>
                          <td>{student.vkr_theme}</td>
                        </tr>
                      ))}
                    </tbody>
                  </Table>
                )}
              </React.Fragment>
            ))}
          </div>
        ))}
      </div>
    </div>
  );
};

export default ViewProtectionSchedulePersons;
